import React, { useState } from 'react';
import './BeadGuide.css';

// 珠子材質數據
const beadMaterials = {
  glass: {
    name: '玻璃珠',
    description: '晶瑩剔透，色彩豐富',
    icon: '/light-pink-bead-ID1.png',
    characteristics: [
      '透明度高，光澤度好',
      '色彩飽和度高',
      '適合製作亮眼的首飾',
      '重量適中，佩戴舒適'
    ],
    care: '避免碰撞，定期清潔',
    price: '中等',
    applications: ['項鍊', '手鍊', '耳環', '髮飾'],
    colors: ['紅色', '藍色', '綠色', '紫色', '黃色', '粉色', '金色', '銀色']
