import React, { useState, useRef, useEffect } from 'react';
import { gsap } from 'gsap';
import './BeadCabinet.css';

/*
 * ========================================
 * 版本2 - 2024-12-12
 * 狀態：完整串珠動畫系統完成
 * 功能：
 * - ✅ 第一顆珠子串珠動畫正常
 * - ✅ 第二顆及後續珠子串珠動畫正常
 * - ✅ 移除浮動效果
 * - ✅ 珠子不會重疊
 * - ✅ 終點位置計算正確
 * - ✅ 使用 0.707 (cos45°) 計算45度旋轉終點
 * - ✅ 間距規則：前大後小35px, 前小後小20px, 前大後大50px, 前小後大40px
 * - ✅ 動畫速度：1.5秒滑動，無等待時間
 * - ✅ 所有珠子參照第一顆終點位置計算
 * ========================================
 */

// 浮空珠子組件
const FloatingBead = ({ drawer, drawerId, onClose, onDragStart, onDragEnd }) => {
  const [position, setPosition] = useState({ top: 0, left: 0 });
  const [isDragging, setIsDragging] = useState(false);

  useEffect(() => {
    const updatePosition = () => {
      const drawerElement = document.querySelector(`[data-drawer-id="${drawerId}"]`);
      if (drawerElement) {
        const rect = drawerElement.getBoundingClientRect();

        setPosition({
          top: rect.top - 150, // 在抽屜上方150px，給說明視窗留更多空間
          left: rect.left + rect.width / 2
        });
      }
    };

    // 立即更新位置
    updatePosition();

    // 監聽視窗大小變化
    window.addEventListener('resize', updatePosition);

    return () => {
      window.removeEventListener('resize', updatePosition);
    };
  }, [drawerId]);

  // 檢查是否為薄荷綠珠子，使用擬真圖片
  const isMintGreenBead = drawer.name === '薄荷綠';

  // 調試訊息
  console.log('浮空珠子檢查:', {
    drawerName: drawer.name,
    isMintGreen: isMintGreenBead,
    shouldUseImage: isMintGreenBead
  });

  return (
    <div 
      className="floating-bead-container"
      data-type={drawer.type}
      style={{
        top: `${position.top}px`,
        left: `${position.left}px`,
        transform: 'translateX(-50%)',
        zIndex: 2000,
        pointerEvents: 'none'
      }}
    >
      <div className="floating-bead-info" style={{ pointerEvents: 'auto' }}>
        <div className="floating-bead-text">
          <h4>{drawer.name}</h4>
          <small>{drawer.type}</small>
        </div>
        <button 
          className="close-floating-btn"
          onClick={onClose}
        >
          ✕
        </button>
      </div>
      <div
        className={`floating-bead ${isDragging ? 'dragging' : ''} ${isMintGreenBead ? 'mint-green-floating-bead' : ''}`}
        data-type={drawer.type}
        draggable
        onClick={(e) => {
          e.stopPropagation();
          console.log('浮空珠子被點擊:', drawer.name);
        }}
        onDragStart={(e) => {
          console.log('開始拖曳浮空珠子:', drawer.name);
          setIsDragging(true);
          onDragStart(drawer, e);
        }}
        onDragEnd={(e) => {
          console.log('結束拖曳浮空珠子:', drawer.name);
          setIsDragging(false);
          onDragEnd(e);
        }}
        style={{
