// 保存的設計組件
const SavedDesigns = () => {
  const [savedDesigns, setSavedDesigns] = useState([]);

  // 根據珠子類型獲取顏色
  const getBeadColor = (bead) => {
    const colorMap = {
      '玻璃珠': '#FF6B9D',
      '米珠': '#F5F5DC',
      '水晶珠': '#9370DB',
      '木珠': '#8B4513',
      '過渡珠': '#FFD700',
      '珍珠': '#F0F8FF'
    };
    
    return colorMap[bead.type] || '#ccc';
  };

  useEffect(() => {
    // 從 localStorage 讀取保存的設計
    const designs = JSON.parse(localStorage.getItem('beadDesigns') || '[]');
    setSavedDesigns(designs);
  }, []);

  const deleteDesign = (designId) => {
    const updatedDesigns = savedDesigns.filter(design => design.id !== designId);
    localStorage.setItem('beadDesigns', JSON.stringify(updatedDesigns));
    setSavedDesigns(updatedDesigns);
  };

  if (savedDesigns.length === 0) {
    return (
      <div className="saved-designs-empty">
        <div className="empty-content">
          <h3>✨ 還沒有保存的設計</h3>
          <p>在珠子收納櫃中串珠後，點擊「💾 保存設計」按鈕來保存您的創作！</p>
          <div className="empty-icon">💡</div>
        </div>
      </div>
    );
  }

  return (
    <div className="saved-designs">
      <h3>💾 已保存的設計 ({savedDesigns.length})</h3>
      <div className="designs-grid">
        {savedDesigns.map((design) => (
          <div key={design.id} className="design-card">
            <div className="design-content">
              <div className="design-info">
                <div className="design-header">
                  <h4>{design.name}</h4>
                  <button 
                    className="delete-design-btn"
                    onClick={() => deleteDesign(design.id)}
                    title="刪除設計"
                  >
                    🗑️
                  </button>
                </div>
                <div className="design-details">
                  <div className="design-meta">
                    <span className="meta-tag">線材: {design.stringWidth}</span>
                    <span className="meta-tag">長度: {design.stringLength}</span>
                    <span className="meta-tag">珠子: {design.beads.length} 顆</span>
                  </div>
                  <div className="design-beads">
                    <h5>使用的珠子:</h5>
                    <div className="beads-list">
                      {design.beads.map((bead, index) => (
                        <span key={index} className="bead-tag">
                          {bead.name} ({bead.type})
                        </span>
                      ))}
                    </div>
                  </div>
                  <div className="design-date">
                    創建於: {new Date(design.createdAt).toLocaleDateString('zh-TW')}
                  </div>
                </div>
              </div>
              <div className="design-image">
                <div className="bracelet-preview">
                                    <div className="bracelet-circle">
                    <svg className="bracelet-string" width="120" height="120" viewBox="0 0 120 120">
                      <circle
                        cx="60"
                        cy="60"
                        r="48"
                        fill="none"
                        stroke="#8B4513"
                        strokeWidth={design.stringWidth === 'thin' ? '2' : 
                                    design.stringWidth === 'medium' ? '3' : '4'}
                        strokeDasharray="2,2"
                        opacity="0.8"
                      />
                    </svg>
                    <div className="bracelet-beads">
                      {design.beads.slice(0, 8).map((bead, index) => {
                        // 計算珠子在圓形線條上的位置
                        const totalBeads = Math.min(design.beads.length, 8);
                        const angle = (index * 360) / totalBeads;
                        const radius = 32;
                        
                        // 從12點鐘方向開始，順時針排列
                        const x = 40 + Math.cos((angle - 90) * Math.PI / 180) * radius;
                        const y = 40 + Math.sin((angle - 90) * Math.PI / 180) * radius;
                        
                        return (
                          <div 
                            key={index} 
                            className="preview-bead"
                            style={{
                              backgroundColor: getBeadColor(bead),
                              left: `${x}px`,
                              top: `${y}px`
                            }}
                            title={`${bead.name} (${bead.type})`}
                          />
                        );
                      })}
                    </div>
                  </div>
                  <div className="bracelet-info">
                    <span className="bracelet-length">{design.stringLength}</span>
                    <span className="bracelet-width">{design.stringWidth}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

