import React, { useState, useRef, useEffect } from 'react';
import './BeadRating.css';

const BeadRating = () => {
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [showResult, setShowResult] = useState(false);
  const [currentMessage, setCurrentMessage] = useState('');
  const [fullMessage, setFullMessage] = useState('');
  const [scores, setScores] = useState({
    love: 0,
    windfall: 0,
    regularIncome: 0,
    career: 0,
    health: 0
  });
  const [savedDesign, setSavedDesign] = useState(null);

  // 通靈師的完整建議
  const psychicAdvice = `你的串珠作品展現了獨特的藝術天賦和內在智慧。這件作品不僅是一件美麗的飾品，更是你內心世界的真實寫照。從愛情運勢來看，紅色珠子的點綴象徵著熱情與勇氣，預示著美好的愛情即將到來，在接下來的三個月內，你將遇到一位與你靈魂共鳴的人。在財富方面，藍色珠子的能量與水元素相呼應，預示著流動的財富即將到來，建議你保持開放的心態，留意身邊的投資機會，但切記不要過於貪心，穩健的理財方式會為你帶來意想不到的收穫。事業發展上，串珠的對稱排列反映了你對工作的認真態度，而金色珠子的點綴則象徵著豐厚的回報，你的努力將得到認可，升職加薪的機會就在眼前。從創造力角度來看，綠色珠子的能量與成長相呼應，預示著你的事業將進入快速發展期，新的項目機會將接踵而至。在健康方面，整體能量非常和諧，你的串珠作品散發著平衡與健康的氣息，紫色珠子的能量與靈性相呼應，預示著你的身心狀態將達到最佳狀態。建議你將這份創造力運用到生活的各個方面，相信自己的直覺，勇敢追求夢想，保持規律的作息，多接觸大自然。記住，每個珠子都承載著獨特的能量，就像你人生中的每個選擇都蘊含著無限可能。`;

  // 檢查是否有保存的串珠設計
  useEffect(() => {
    const savedDesignData = localStorage.getItem('savedBeadDesign');
    if (savedDesignData) {
      try {
        const design = JSON.parse(savedDesignData);
        setSavedDesign(design);
        console.log('載入保存的串珠設計:', design);
      } catch (error) {
        console.error('解析保存的設計數據失敗:', error);
      }
    }
  }, []);

  // 模擬AI分析過程
  const startAnalysis = () => {
    if (!savedDesign) {
      alert('請先在珠子收納櫃保存串珠設計！');
      return;
    }

    setIsAnalyzing(true);
    setCurrentMessage('');
    setShowResult(false);

    // 模擬分析過程
    setTimeout(() => {
      // 生成隨機評分 - 調整範圍確保在格線內
      const newScores = {
        love: Math.floor(Math.random() * 3) + 6, // 6-8分
        windfall: Math.floor(Math.random() * 3) + 6, // 6-8分
        regularIncome: Math.floor(Math.random() * 3) + 6, // 6-8分
        career: Math.floor(Math.random() * 3) + 6, // 6-8分
        health: Math.floor(Math.random() * 3) + 6 // 6-8分
      };
      setScores(newScores);

      // 逐字顯示建議
      let index = 0;
      const typeWriter = () => {
        if (index < psychicAdvice.length) {
          setCurrentMessage(psychicAdvice.substring(0, index + 1));
          index++;
          setTimeout(typeWriter, 50);
        } else {
          setShowResult(true);
          setIsAnalyzing(false);
        }
      };
      typeWriter();
    }, 2000);
  };

  // 清除保存的設計
  const clearSavedDesign = () => {
    localStorage.removeItem('savedBeadDesign');
    setSavedDesign(null);
    setShowResult(false);
    setCurrentMessage('');
    setFullMessage('');
  };

  // 前往珠子收納櫃創建新設計
  const goToBeadCabinet = () => {
    window.location.href = '/';
  };

  // 返回主頁
  const goHome = () => {
    window.open('http://127.0.0.1:5500/index.html', '_self');
  };

  // 計算安全的評分點位置，確保在格線內
  const getSafeScorePosition = (score, baseX, baseY) => {
    // 評分範圍：6-8分，對應到網格的不同層級
    // 6分 = 最內層，7分 = 中層，8分 = 外層
    const level = score - 6; // 0, 1, 2
    const maxRadius = 15; // 最大半徑，確保在格線內
    
    // 計算位置，限制在安全範圍內
    const x = baseX + (level * maxRadius);
    const y = baseY + (level * maxRadius);
    
    return { x, y };
  };

  // 計算六邊形評分區域的頂點位置
  const getHexagonScorePoints = (scores) => {
    // 六邊形的六個頂點，從頂部順時針排列
    const centerX = 150;
    const centerY = 150;
    
    // 計算每個軸線上的評分點位置
    const points = [
      // 頂部：設計感 (y軸負方向)
      { x: centerX, y: centerY - (scores.love - 5) * 20 },
      
      // 右上：愛情 (右上方向)
      { x: centerX + (scores.windfall - 5) * 17.32, y: centerY - (scores.windfall - 5) * 10 },
      
      // 右下：偏財 (右下方向)
      { x: centerX + (scores.regularIncome - 5) * 17.32, y: centerY + (scores.regularIncome - 5) * 10 },
      
      // 底部：正財 (y軸正方向)
      { x: centerX, y: centerY + (scores.career - 5) * 20 },
      
      // 左下：事業 (左下方向)
      { x: centerX - (scores.health - 5) * 17.32, y: centerY + (scores.health - 5) * 10 },
      
      // 左上：健康 (左上方向)
      { x: centerX - (scores.love - 5) * 17.32, y: centerY - (scores.love - 5) * 10 }
    ];
    
    return points;
  };


  return (
    <div className="bead-rating-container">
      {/* 返回按鈕 */}
      <div className="rating-header">
        <button className="back-btn" onClick={goHome}>
          🏠 返回主頁
        </button>
        <h1>⭐ 串珠評分區 ⭐</h1>
      </div>

      <div className="rating-content">
        {/* 左側：通靈師和對話框 */}
        <div className="left-panel">
          {/* 左上：對話框 */}
          <div className="dialogue-box">
            {!isAnalyzing && !showResult && (
              <div className="welcome-message">
                <p>歡迎來到神秘占卜殿堂，讓我來解讀你的串珠設計...</p>
                {savedDesign ? (
                  <p>我看到你精心設計的串珠作品，讓我為你揭示其中的奧秘。</p>
                ) : (
                  <p>請先在珠子收納櫃創建並保存您的串珠設計，我將為你揭示其中的奧秘。</p>
                )}
              </div>
            )}

            {isAnalyzing && (
              <div className="analyzing-message">
                <div className="crystal-animation">🔮</div>
                <p>我正在解讀你的串珠作品...</p>
                <p>水晶球正在揭示命運的奧秘...</p>
              </div>
            )}

            {showResult && (
              <div className="result-message">
                <p className="advice-text">{currentMessage}</p>
                {currentMessage.length < psychicAdvice.length && (
                  <div className="typing-indicator">|</div>
                )}
              </div>
            )}
          </div>

          {/* 左中下：AI虛擬通靈師 */}
          <div className="psychic-medium">
            <div className="psychic-avatar">
              <div className="psychic-character">
                <img 
                  src="/psychic-medium.png" 
                  alt="神秘通靈師" 
                  className="psychic-image"
                />
              </div>
              <div className="psychic-title">神秘通靈師</div>
              <div className="psychic-subtitle">✨ 能量分析專家 ✨</div>
            </div>
          </div>
        </div>

        {/* 右側：作品上傳和雷達圖 */}
        <div className="right-panel">
          {/* 串珠設計展示 */}
          <div className="bead-design-section">
            <h3>📿 你的串珠設計</h3>
            {savedDesign ? (
              <div className="design-display">
                <div className="design-info">
                  <h4>{savedDesign.designName}</h4>
                  <p>包含 {savedDesign.beads.length} 顆珠子</p>
                  <p>創建時間: {new Date(savedDesign.timestamp).toLocaleString()}</p>
                </div>
                
                {/* 串珠設計視覺化 */}
                <div className="bead-design-visualization">
                  <div className="bead-string-preview">
                    {savedDesign.beads.map((bead, index) => (
                      <div 
                        key={index}
                        className="design-bead"
                        style={{ 
                          backgroundColor: bead.color,
                          animationDelay: `${index * 0.1}s`
                        }}
                        title={`${bead.name} - ${bead.type}`}
                      >
                        <div className="bead-shine"></div>
                        <div className="bead-reflection"></div>
                        <div className="bead-glow" style={{ boxShadow: `0 0 15px ${bead.shine}` }}></div>
                      </div>
                    ))}
                  </div>
                </div>
                
                <div className="design-controls">
                  <button 
                    className="analyze-btn" 
                    onClick={startAnalysis}
                    disabled={isAnalyzing}
                  >
                    {isAnalyzing ? '🔮 分析中...' : '🔮 開始分析'}
                  </button>
                  <button 
                    className="clear-design-btn" 
                    onClick={clearSavedDesign}
                  >
                    🗑️ 清除設計
                  </button>
                </div>
              </div>
            ) : (
              <div className="no-design-message">
                <div className="no-design-icon">🎨</div>
                <h4>還沒有串珠設計</h4>
                <p>請先到珠子收納櫃創建並保存您的串珠設計</p>
                <button 
                  className="go-to-cabinet-btn" 
                  onClick={goToBeadCabinet}
                >
                  🏠 前往珠子收納櫃
                </button>
              </div>
            )}
          </div>

          {/* 雷達圖 */}
          <div className="radar-chart-section">
            <h3>🌟 能量評分圖</h3>
            <div className="radar-chart">
              {/* SVG雷達圖 */}
              <svg 
                className="radar-svg" 
                viewBox="0 0 300 300" 
                width="300" 
                height="300"
              >
                {/* 背景網格 - 三個同心六邊形 */}
                <polygon 
                  points="150,50 250,100 250,200 150,250 50,200 50,100" 
                  fill="none" 
                  stroke="rgba(255,255,255,0.3)" 
                  strokeWidth="1"
                />
                <polygon 
                  points="150,80 220,120 220,180 150,220 80,180 80,120" 
                  fill="none" 
                  stroke="rgba(255,255,255,0.3)" 
                  strokeWidth="1"
                />
                <polygon 
                  points="150,110 190,140 190,160 150,190 110,160 110,140" 
                  fill="none" 
                  stroke="rgba(255,255,255,0.3)" 
                  strokeWidth="1"
                />
                
                {/* 軸線 - 從中心到六個頂點 */}
                <line x1="150" y1="150" x2="150" y2="50" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                <line x1="150" y1="150" x2="250" y2="100" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                <line x1="150" y1="150" x2="250" y2="200" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                <line x1="150" y1="150" x2="150" y2="250" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                <line x1="150" y1="150" x2="50" y2="200" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                <line x1="150" y1="150" x2="50" y2="100" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                
                {/* AI評分結果填充區域 - 根據評分自動生成顏色 */}
                {showResult && (
                  <polygon 
                    points={`
                      ${getSafeScorePosition(scores.love, 150, 50).x},${getSafeScorePosition(scores.love, 150, 50).y}
                      ${getSafeScorePosition(scores.windfall, 150, 100).x},${getSafeScorePosition(scores.windfall, 150, 100).y}
                      ${getSafeScorePosition(scores.regularIncome, 150, 200).x},${getSafeScorePosition(scores.regularIncome, 150, 200).y}
                      ${getSafeScorePosition(scores.career, 150, 250).x},${getSafeScorePosition(scores.career, 150, 250).y}
                      ${getSafeScorePosition(scores.health, 150, 200).x},${getSafeScorePosition(scores.health, 150, 200).y}
                      ${getSafeScorePosition(scores.love, 150, 100).x},${getSafeScorePosition(scores.love, 150, 100).y}
                    `}
                    fill="rgba(138, 43, 226, 0.6)" 
                    stroke="rgba(138, 43, 226, 0.8)" 
                    strokeWidth="2"
                  />
                )}
                
                {/* 評分點 */}
                {showResult && (
                  <>
                    <circle cx={getSafeScorePosition(scores.love, 150, 50).x} cy={getSafeScorePosition(scores.love, 150, 50).y} r="4" fill="#ffd700" />
                    <circle cx={getSafeScorePosition(scores.windfall, 150, 100).x} cy={getSafeScorePosition(scores.windfall, 150, 100).y} r="4" fill="#ffd700" />
                    <circle cx={getSafeScorePosition(scores.regularIncome, 150, 200).x} cy={getSafeScorePosition(scores.regularIncome, 150, 200).y} r="4" fill="#ffd700" />
                    <circle cx={getSafeScorePosition(scores.career, 150, 250).x} cy={getSafeScorePosition(scores.career, 150, 250).y} r="4" fill="#ffd700" />
                    <circle cx={getSafeScorePosition(scores.health, 150, 200).x} cy={getSafeScorePosition(scores.health, 150, 200).y} r="4" fill="#ffd700" />
                    <circle cx={getSafeScorePosition(scores.love, 150, 100).x} cy={getSafeScorePosition(scores.love, 150, 100).y} r="4" fill="#ffd700" />
                  </>
                )}
                
                {/* 軸線標籤 */}
                <text x="150" y="35" className="axis-label" textAnchor="middle">設計感</text>
                <text x="265" y="105" className="axis-label" textAnchor="start">愛情</text>
                <text x="265" y="205" className="axis-label" textAnchor="start">偏財</text>
                <text x="150" y="275" className="axis-label" textAnchor="middle">正財</text>
                <text x="35" y="205" className="axis-label" textAnchor="end">事業</text>
                <text x="35" y="105" className="axis-label" textAnchor="end">健康</text>
                
                {/* 評分標籤 */}
                {showResult && (
                  <>
                    <text x={getSafeScorePosition(scores.love, 150, 50).x} y={getSafeScorePosition(scores.love, 150, 50).y - 10} className="score-label" textAnchor="middle">{scores.love}</text>
                    <text x={getSafeScorePosition(scores.windfall, 150, 100).x + 10} cy={getSafeScorePosition(scores.windfall, 150, 100).y} className="score-label" textAnchor="middle">{scores.windfall}</text>
                    <text x={getSafeScorePosition(scores.regularIncome, 150, 200).x + 10} cy={getSafeScorePosition(scores.regularIncome, 150, 200).y} className="score-label" textAnchor="middle">{scores.regularIncome}</text>
                    <text x={getSafeScorePosition(scores.career, 150, 250).x} y={getSafeScorePosition(scores.career, 150, 250).y + 15} className="score-label" textAnchor="middle">{scores.career}</text>
                    <text x={getSafeScorePosition(scores.health, 150, 200).x - 10} cy={getSafeScorePosition(scores.health, 150, 200).y} className="score-label" textAnchor="middle">{scores.health}</text>
                    <text x={getSafeScorePosition(scores.love, 150, 100).x - 10} cy={getSafeScorePosition(scores.love, 150, 100).y} className="score-label" textAnchor="middle">{scores.love}</text>
                  </>
                )}
              </svg>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BeadRating;
