import React, { useState, useRef, useEffect } from 'react';
import './BeadRating.css';

const BeadRating = () => {
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [showResult, setShowResult] = useState(false);
  const [currentMessage, setCurrentMessage] = useState('');
  const [fullMessage, setFullMessage] = useState('');
  const [scores, setScores] = useState({
    love: 0,
    windfall: 0,
    regularIncome: 0,
    career: 0,
    health: 0
  });
  const [savedDesign, setSavedDesign] = useState(null);

  // 通靈師的完整建議
  const psychicAdvice = `你的串珠作品展現了獨特的藝術天賦和內在智慧。這件作品不僅是一件美麗的飾品，更是你內心世界的真實寫照。從愛情運勢來看，紅色珠子的點綴象徵著熱情與勇氣，預示著美好的愛情即將到來，在接下來的三個月內，你將遇到一位與你靈魂共鳴的人。在財富方面，藍色珠子的能量與水元素相呼應，預示著流動的財富即將到來，建議你保持開放的心態，留意身邊的投資機會，但切記不要過於貪心，穩健的理財方式會為你帶來意想不到的收穫。事業發展上，串珠的對稱排列反映了你對工作的認真態度，而金色珠子的點綴則象徵著豐厚的回報，你的努力將得到認可，升職加薪的機會就在眼前。從創造力角度來看，綠色珠子的能量與成長相呼應，預示著你的事業將進入快速發展期，新的項目機會將接踵而至。在健康方面，整體能量非常和諧，你的串珠作品散發著平衡與健康的氣息，紫色珠子的能量與靈性相呼應，預示著你的身心狀態將達到最佳狀態。建議你將這份創造力運用到生活的各個方面，相信自己的直覺，勇敢追求夢想，保持規律的作息，多接觸大自然。記住，每個珠子都承載著獨特的能量，就像你人生中的每個選擇都蘊含著無限可能。`;

  // 檢查是否有保存的串珠設計
  useEffect(() => {
    const savedDesignData = localStorage.getItem('savedBeadDesign');
    if (savedDesignData) {
      try {
        const design = JSON.parse(savedDesignData);
        setSavedDesign(design);
        console.log('載入保存的串珠設計:', design);
      } catch (error) {
        console.error('解析保存的設計數據失敗:', error);
      }
    }
  }, []);

  // 15個抽屜的雙面向評分系統
  const getBeadDualCategoryAndScore = (beadId) => {
    // 左邊15個抽屜，每個珠子有兩個面向的評分
    const beadCategories = {
      // 第一排 (左至右)
      1: { 
        primary: { category: 'love', name: '粉晶', score: 5, description: '愛情與和諧' },
        secondary: { category: 'health', name: '粉晶', score: 3, description: '情感健康' }
      },
      2: { 
        primary: { category: 'windfall', name: '黃水晶', score: 5, description: '財富與成功' },
        secondary: { category: 'career', name: '黃水晶', score: 4, description: '事業自信' }
      },
      3: { 
        primary: { category: 'regularIncome', name: '白水晶', score: 5, description: '淨化與平衡' },
        secondary: { category: 'health', name: '白水晶', score: 4, description: '身心淨化' }
      },
      4: { 
        primary: { category: 'career', name: '綠松石', score: 5, description: '保護與溝通' },
        secondary: { category: 'windfall', name: '綠松石', score: 3, description: '財運保護' }
      },
      5: { 
        primary: { category: 'health', name: '紫水晶', score: 5, description: '靈性與健康' },
        secondary: { category: 'love', name: '紫水晶', score: 4, description: '智慧愛情' }
      },
      
      // 第二排 (左至右)
      6: { 
        primary: { category: 'love', name: '玫瑰石', score: 4, description: '浪漫與熱情' },
        secondary: { category: 'career', name: '玫瑰石', score: 3, description: '人際魅力' }
      },
      7: { 
        primary: { category: 'windfall', name: '紅瑪瑙', score: 4, description: '活力與勇氣' },
        secondary: { category: 'health', name: '紅瑪瑙', score: 3, description: '生命力' }
      },
      8: { 
        primary: { category: 'regularIncome', name: '青金石', score: 4, description: '智慧與財富' },
        secondary: { category: 'love', name: '青金石', score: 3, description: '智慧愛情' }
      },
      9: { 
        primary: { category: 'career', name: '黑曜石', score: 4, description: '保護與力量' },
        secondary: { category: 'regularIncome', name: '黑曜石', score: 3, description: '財運保護' }
      },
      10: { 
        primary: { category: 'health', name: '綠松石', score: 4, description: '保護與平衡' },
        secondary: { category: 'windfall', name: '綠松石', score: 3, description: '財運平衡' }
      },
      
      // 第三排 (左至右)
      11: { 
        primary: { category: 'love', name: '月光石', score: 3, description: '溫柔與包容' },
        secondary: { category: 'regularIncome', name: '月光石', score: 2, description: '財運和諧' }
      },
      12: { 
        primary: { category: 'windfall', name: '琥珀', score: 3, description: '智慧與財運' },
        secondary: { category: 'career', name: '琥珀', score: 2, description: '事業智慧' }
      },
      13: { 
        primary: { category: 'regularIncome', name: '海藍寶', score: 3, description: '平靜與穩定' },
        secondary: { category: 'health', name: '海藍寶', score: 2, description: '情緒穩定' }
      },
      14: { 
        primary: { category: 'career', name: '紫水晶', score: 3, description: '智慧與靈性' },
        secondary: { category: 'love', name: '紫水晶', score: 2, description: '靈性愛情' }
      },
      15: { 
        primary: { category: 'health', name: '白水晶', score: 3, description: '淨化與療癒' },
        secondary: { category: 'windfall', name: '白水晶', score: 2, description: '財運淨化' }
      }
    };
    
    return beadCategories[beadId] || { 
      primary: { category: 'unknown', name: '未知', score: 1, description: '未知能量' },
      secondary: { category: 'unknown', name: '未知', score: 1, description: '未知能量' }
    };
  };

  // 根據珠子設計分析評分
  const analyzeBeadDesign = (design) => {
    const { beads } = design;
    let scores = {
      love: 0,
      windfall: 0,
      regularIncome: 0,
      career: 0,
      health: 0
    };

    // 記錄每個項目是否已經有第一顆珠子
    let hasFirstBead = {
      love: false,
      windfall: false,
      regularIncome: false,
      career: false,
      health: false
    };

    // 記錄每個項目是否有珠子
    let hasAnyBead = {
      love: false,
      windfall: false,
      regularIncome: false,
      career: false,
      health: false
    };

    // 分析每顆珠子的能量屬性 - 使用21個櫃子的分類系統
    beads.forEach(bead => {
      const { id, name } = bead;
      const beadInfo = getBeadDualCategoryAndScore(id);
      console.log('分析珠子:', { id, name, category: beadInfo.primary.category, score: beadInfo.primary.score });
      
      // 根據櫃子分類分配評分
      switch (beadInfo.primary.category) {
        case 'love':
          hasAnyBead.love = true;
          if (!hasFirstBead.love) {
            scores.love += 5; // 第一顆愛情珠子加5分
            hasFirstBead.love = true;
          } else {
            scores.love += beadInfo.primary.score; // 後續珠子按櫃子分數加分
          }
          break;
          
        case 'windfall':
          hasAnyBead.windfall = true;
          if (!hasFirstBead.windfall) {
            scores.windfall += 5; // 第一顆偏財珠子加5分
            hasFirstBead.windfall = true;
          } else {
            scores.windfall += beadInfo.primary.score; // 後續珠子按櫃子分數加分
          }
          break;
          
        case 'regularIncome':
          hasAnyBead.regularIncome = true;
          if (!hasFirstBead.regularIncome) {
            scores.regularIncome += 5; // 第一顆正財珠子加5分
            hasFirstBead.regularIncome = true;
          } else {
            scores.regularIncome += beadInfo.primary.score; // 後續珠子按櫃子分數加分
          }
          break;
          
        case 'career':
          hasAnyBead.career = true;
          if (!hasFirstBead.career) {
            scores.career += 5; // 第一顆事業珠子加5分
            hasFirstBead.career = true;
          } else {
            scores.career += beadInfo.primary.score; // 後續珠子按櫃子分數加分
          }
          break;
          
        case 'health':
          hasAnyBead.health = true;
          if (!hasFirstBead.health) {
            scores.health += 5; // 第一顆健康珠子加5分
            hasFirstBead.health = true;
          } else {
            scores.health += beadInfo.primary.score; // 後續珠子按櫃子分數加分
          }
          break;
          
        case 'comprehensive':
          // 綜合珠子對所有項目都有加分
          hasAnyBead.love = true;
          hasAnyBead.windfall = true;
          hasAnyBead.regularIncome = true;
          hasAnyBead.career = true;
          hasAnyBead.health = true;
          
          if (!hasFirstBead.love) {
            scores.love += 3;
            hasFirstBead.love = true;
          } else {
            scores.love += beadInfo.primary.score * 0.5;
          }
          
          if (!hasFirstBead.windfall) {
            scores.windfall += 3;
            hasFirstBead.windfall = true;
          } else {
            scores.windfall += beadInfo.primary.score * 0.5;
          }
          
          if (!hasFirstBead.regularIncome) {
            scores.regularIncome += 3;
            hasFirstBead.regularIncome = true;
          } else {
            scores.regularIncome += beadInfo.primary.score * 0.5;
          }
          
          if (!hasFirstBead.career) {
            scores.career += 3;
            hasFirstBead.career = true;
          } else {
            scores.career += beadInfo.primary.score * 0.5;
          }
          
          if (!hasFirstBead.health) {
            scores.health += 3;
            hasFirstBead.health = true;
          } else {
            scores.health += beadInfo.primary.score * 0.5;
          }
          break;
          
        default:
          console.log('未知珠子類型:', beadInfo);
          break;
      }
    });
    
    // 為沒有珠子的項目給予基礎分數
    Object.keys(hasAnyBead).forEach(key => {
      if (!hasAnyBead[key]) {
        scores[key] = 3; // 沒有相關珠子時給予3分基礎分
      }
    });
    
    // 根據珠子數量調整評分
    const beadCount = beads.length;
    if (beadCount >= 15) {
      // 珠子數量多，代表設計複雜，整體加分
      Object.keys(scores).forEach(key => {
        scores[key] += 1;
      });
    } else if (beadCount <= 5) {
      // 珠子數量少，代表設計簡潔，某些方面加分
      scores.love += 0.5;
      scores.health += 0.5;
    }
    
    // 確保評分在1-10分範圍內
    Object.keys(scores).forEach(key => {
      scores[key] = Math.max(1, Math.min(10, Math.round(scores[key])));
    });
    
    console.log('最終評分:', scores); // 調試用
    return scores;
  };

  // 根據珠子設計生成個性化建議
  const generatePersonalizedAdvice = (design, scores) => {
    const { beads } = design;
    let advice = '你的串珠作品展現了獨特的藝術天賦和內在智慧。';
    
    // 分析顏色搭配
    const colors = beads.map(bead => bead.color);
    const uniqueColors = [...new Set(colors)];
    const colorCount = uniqueColors.length;
    
    // 根據顏色數量給出肯定
    if (colorCount >= 5) {
      advice += '你大膽運用了豐富的色彩搭配，展現了非凡的創意和勇氣！';
    } else if (colorCount >= 3) {
      advice += '你的色彩搭配和諧優雅，體現了對美學的敏銳感知。';
    } else if (colorCount === 2) {
      advice += '你的雙色搭配簡潔有力，展現了精準的設計眼光。';
    } else {
      advice += '你的單色設計純粹動人，體現了對細節的完美追求。';
    }
    
    // 分析具體顏色搭配
    const hasPink = colors.some(color => color.includes('#EC4899'));
    const hasPurple = colors.some(color => color.includes('#8B5CF6'));
    const hasYellow = colors.some(color => color.includes('#F59E0B'));
    const hasGreen = colors.some(color => color.includes('#06B6D4'));
    const hasWhite = colors.some(color => color.includes('#F3F4F6'));
    const hasRed = colors.some(color => color.includes('#EF4444'));
    
    // 根據顏色搭配給出專業點評
    if (hasPink && hasPurple) {
      advice += '粉晶與紫水晶的搭配非常出色！這種組合既浪漫又神秘，預示著你將在愛情和靈性方面都有重大突破。';
    }
    
    if (hasYellow && hasGreen) {
      advice += '黃水晶與綠松石的組合充滿活力！這代表著財富與成長的完美結合，你的投資眼光和事業發展將相輔相成。';
    }
    
    if (hasRed && hasWhite) {
      advice += '紅瑪瑙與白水晶的搭配極具張力！熱情與純淨的對比展現了你內心的強大力量，這種組合將為你帶來勇氣和淨化。';
    }
    
    if (hasPurple && hasGreen) {
      advice += '紫水晶與綠松石的搭配智慧與自然並存！這預示著你將在事業發展中展現出獨特的創造力和溝通天賦。';
    }
    
    // 根據評分給出具體建議
    if (scores.love >= 8) {
      advice += '從愛情運勢來看，你的設計充滿了愛的氣息！粉晶和紫水晶的能量將為你吸引到靈魂伴侶，在接下來的三個月內，美好的愛情即將到來。';
    } else if (scores.love >= 6) {
      advice += '你的愛情運勢穩步上升，串珠中的愛情能量正在積累，建議你保持開放的心態，愛情會在不經意間降臨。';
    }
    
    if (scores.windfall >= 8) {
      advice += '偏財運勢非常旺盛！黃水晶和綠松石的能量預示著意外的財富機會，建議你留意身邊的投資機會，但切記保持理性。';
    } else if (scores.windfall >= 6) {
      advice += '偏財運勢正在發展中，你的創意和直覺將為你帶來額外的收入來源。';
    }
    
    if (scores.regularIncome >= 8) {
      advice += '正財運勢極佳！綠松石和紫水晶的能量預示著穩定的收入增長，你的努力將得到豐厚的回報。';
    } else if (scores.regularIncome >= 6) {
      advice += '正財運勢穩定，建議你繼續保持當前的理財方式，穩健的投資會帶來可觀的收益。';
    }
    
    if (scores.career >= 8) {
      advice += '事業運勢一片光明！綠松石和紫水晶的組合預示著你將在職場上大放異彩，升職加薪的機會就在眼前。';
    } else if (scores.career >= 6) {
      advice += '事業運勢穩步上升，你的專業能力和創造力正在被認可，新的發展機會即將到來。';
    }
    
    if (scores.health >= 8) {
      advice += '健康運勢非常和諧！綠松石和白水晶的能量預示著你的身心狀態將達到最佳狀態，整體能量非常平衡。';
    } else if (scores.health >= 6) {
      advice += '健康運勢良好，建議你保持規律的作息，多接觸大自然，身心會更加健康。';
    }
    
    // 根據珠子數量給出建議
    const beadCount = beads.length;
    if (beadCount >= 15) {
      advice += '你的設計非常複雜精緻，這展現了你對完美的追求和耐心。建議你將這份專注力運用到生活的各個方面。';
    } else if (beadCount <= 5) {
      advice += '你的設計簡潔有力，這體現了你對本質的深刻理解。有時候，少即是多，你的簡約美學將為你帶來獨特的魅力。';
    }
    
    advice += '記住，每個珠子都承載著獨特的能量，就像你人生中的每個選擇都蘊含著無限可能。相信自己的直覺，勇敢追求夢想！';
    
    return advice;
  };

  // 模擬AI分析過程
  const startAnalysis = () => {
    if (!savedDesign) {
      alert('請先在珠子收納櫃保存串珠設計！');
      return;
    }

    console.log('開始分析，保存的設計:', savedDesign); // 調試用

    setIsAnalyzing(true);
    setCurrentMessage('');
    setShowResult(false);

    // 模擬分析過程
    setTimeout(() => {
      // 根據實際珠子設計分析評分
      const newScores = analyzeBeadDesign(savedDesign);
      console.log('分析完成，新評分:', newScores); // 調試用
      
      // 強制更新評分狀態
      setScores(newScores);

      // 根據珠子設計生成個性化建議
      const personalizedAdvice = generatePersonalizedAdvice(savedDesign, newScores);
      setFullMessage(personalizedAdvice);

      // 逐字顯示建議
      let index = 0;
      const typeWriter = () => {
        if (index < personalizedAdvice.length) {
          setCurrentMessage(personalizedAdvice.substring(0, index + 1));
          index++;
          setTimeout(typeWriter, 50);
        } else {
          setShowResult(true);
          setIsAnalyzing(false);
          console.log('分析完成，顯示結果，評分:', newScores); // 調試用
        }
      };
      typeWriter();
    }, 2000);
  };

  // 強制重新計算評分（用於調試）
  const forceRecalculateScores = () => {
    if (!savedDesign) {
      alert('沒有保存的設計！');
      return;
    }
    
    console.log('強制重新計算評分...'); // 調試用
    const newScores = analyzeBeadDesign(savedDesign);
    console.log('重新計算的評分:', newScores); // 調試用
    setScores(newScores);
    
    // 強制重新渲染
    setShowResult(false);
    setTimeout(() => {
      setShowResult(true);
    }, 100);
  };

  // 清除保存的設計
  const clearSavedDesign = () => {
    localStorage.removeItem('savedBeadDesign');
    setSavedDesign(null);
    setShowResult(false);
    setCurrentMessage('');
    setFullMessage('');
  };

  // 前往珠子收納櫃創建新設計
  const goToBeadCabinet = () => {
    window.location.href = '/';
  };

  // 返回主頁
  const goHome = () => {
    window.open('http://127.0.0.1:5500/index.html', '_self');
  };

  // 計算安全的評分點位置，確保在格線內
  const getSafeScorePosition = (score, baseX, baseY) => {
    // 評分範圍：6-8分，對應到網格的不同層級
    // 6分 = 最內層，7分 = 中層，8分 = 外層
    const level = score - 6; // 0, 1, 2
    const maxRadius = 15; // 最大半徑，確保在格線內
    
    // 計算位置，限制在安全範圍內
    const x = baseX + (level * maxRadius);
    const y = baseY + (level * maxRadius);
    
    return { x, y };
  };

  // 計算六邊形評分區域的頂點位置
  const getHexagonScorePoints = (scores) => {
    // 六邊形的六個頂點，從頂部順時針排列
    const centerX = 150;
    const centerY = 150;
    
    // 評分範圍：1-10分，0分在圓心，10分在頂點
    // 計算每個軸線上的評分點位置
    const points = [
      // 頂部：設計感 (y軸負方向)
      { x: centerX, y: centerY - (scores.love / 10) * 100 },
      
      // 右上：愛情 (右上方向，30度角)
      { x: centerX + (scores.windfall / 10) * 86.6, y: centerY - (scores.windfall / 10) * 50 },
      
      // 右下：偏財 (右下方向，30度角)
      { x: centerX + (scores.regularIncome / 10) * 86.6, y: centerY + (scores.regularIncome / 10) * 50 },
      
      // 底部：正財 (y軸正方向)
      { x: centerX, y: centerY + (scores.career / 10) * 100 },
      
      // 左下：事業 (左下方向，30度角)
      { x: centerX - (scores.regularIncome / 10) * 86.6, y: centerY + (scores.regularIncome / 10) * 50 },
      
      // 左上：健康 (左上方向，30度角)
      { x: centerX - (scores.health / 10) * 86.6, y: centerY - (scores.health / 10) * 50 }
    ];
    
    return points;
  };


  return (
    <div className="bead-rating-container">
      {/* 返回按鈕 */}
      <div className="rating-header">
        <button className="back-btn" onClick={goHome}>
          🏠 返回主頁
        </button>
        <h1>⭐ 串珠評分區 ⭐</h1>
      </div>

      <div className="rating-content">
        {/* 左側：通靈師和對話框 */}
        <div className="left-panel">
          {/* 左上：對話框 */}
          <div className="dialogue-box">
            {!isAnalyzing && !showResult && (
              <div className="welcome-message">
                <p>歡迎來到神秘占卜殿堂，讓我來解讀你的串珠設計...</p>
                {savedDesign ? (
                  <p>我看到你精心設計的串珠作品，讓我為你揭示其中的奧秘。</p>
                ) : (
                  <p>請先在珠子收納櫃創建並保存您的串珠設計，我將為你揭示其中的奧秘。</p>
                )}
              </div>
            )}

            {isAnalyzing && (
              <div className="analyzing-message">
                <div className="crystal-animation">🔮</div>
                <p>我正在解讀你的串珠作品...</p>
                <p>水晶球正在揭示命運的奧秘...</p>
              </div>
            )}

            {showResult && (
              <div className="result-message">
                <p className="advice-text">{currentMessage}</p>
                {currentMessage.length < fullMessage.length && (
                  <div className="typing-indicator">|</div>
                )}
              </div>
            )}
          </div>

          {/* 左中下：AI虛擬通靈師 */}
          <div className="psychic-medium">
            <div className="psychic-avatar">
              <div className="psychic-character">
                <img 
                  src="/psychic-medium.png" 
                  alt="神秘通靈師" 
                  className="psychic-image"
                />
              </div>
              <div className="psychic-title">神秘通靈師</div>
              <div className="psychic-subtitle">✨ 能量分析專家 ✨</div>
            </div>
          </div>
        </div>

        {/* 右側：作品上傳和雷達圖 */}
        <div className="right-panel">
          {/* 串珠設計展示 */}
          <div className="bead-design-section">
            <h3>📿 你的串珠設計</h3>
            {savedDesign ? (
              <div className="design-display">
                <div className="design-info">
                  <h4>{savedDesign.designName}</h4>
                  <p>包含 {savedDesign.beads.length} 顆珠子</p>
                  <p>創建時間: {new Date(savedDesign.timestamp).toLocaleString()}</p>
                </div>
                
                {/* 串珠設計視覺化 */}
                <div className="bead-design-visualization">
                  <div className="bead-string-preview">
                    {savedDesign.beads.map((bead, index) => (
                      <div 
                        key={index}
                        className="design-bead"
                        style={{ 
                          backgroundColor: bead.color,
                          animationDelay: `${index * 0.1}s`
                        }}
                        title={`${bead.name} - ${bead.type}`}
                      >
                        <div className="bead-shine"></div>
                        <div className="bead-reflection"></div>
                        <div className="bead-glow" style={{ boxShadow: `0 0 15px ${bead.shine}` }}></div>
                      </div>
                    ))}
                  </div>
                </div>
                
                <div className="design-controls">
                  <button 
                    className="analyze-btn" 
                    onClick={startAnalysis}
                    disabled={isAnalyzing}
                  >
                    {isAnalyzing ? '🔮 分析中...' : '🔮 開始分析'}
                  </button>
                  <button 
                    className="clear-design-btn" 
                    onClick={clearSavedDesign}
                  >
                    🗑️ 清除設計
                  </button>
                  <button 
                    className="force-recalculate-btn" 
                    onClick={forceRecalculateScores}
                  >
                    🔄 強制重新計算評分
                  </button>
                </div>
              </div>
            ) : (
              <div className="no-design-message">
                <div className="no-design-icon">🎨</div>
                <h4>還沒有串珠設計</h4>
                <p>請先到珠子收納櫃創建並保存您的串珠設計</p>
                <button 
                  className="go-to-cabinet-btn" 
                  onClick={goToBeadCabinet}
                >
                  🏠 前往珠子收納櫃
                </button>
              </div>
            )}
          </div>

          {/* 雷達圖 */}
          <div className="radar-chart-section">
            <h3>🌟 能量評分圖</h3>
            
            {/* 調試顯示 - 顯示當前評分狀態 */}
            {showResult && (
              <div className="debug-scores" style={{ 
                background: 'rgba(0,0,0,0.7)', 
                padding: '10px', 
                margin: '10px 0', 
                borderRadius: '5px',
                fontSize: '12px',
                color: 'white'
              }}>
                <strong>當前評分狀態:</strong><br/>
                設計感: {scores.love} | 愛情: {scores.windfall} | 偏財: {scores.regularIncome}<br/>
                正財: {scores.career} | 事業: {scores.regularIncome} | 健康: {scores.health}
              </div>
            )}
            
            <div className="radar-chart">
              {/* SVG雷達圖 */}
              <svg 
                className="radar-svg" 
                viewBox="0 0 300 300" 
                width="300" 
                height="300"
              >
                {/* 背景網格 - 三個同心六邊形 */}
                <polygon 
                  points="150,50 250,100 250,200 150,250 50,200 50,100" 
                  fill="none" 
                  stroke="rgba(255,255,255,0.3)" 
                  strokeWidth="1"
                />
                <polygon 
                  points="150,80 220,120 220,180 150,220 80,180 80,120" 
                  fill="none" 
                  stroke="rgba(255,255,255,0.3)" 
                  strokeWidth="1"
                />
                <polygon 
                  points="150,110 190,140 190,160 150,190 110,160 110,140" 
                  fill="none" 
                  stroke="rgba(255,255,255,0.3)" 
                  strokeWidth="1"
                />
                
                {/* 軸線 - 從中心到六個頂點 */}
                <line x1="150" y1="150" x2="150" y2="50" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                <line x1="150" y1="150" x2="250" y2="100" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                <line x1="150" y1="150" x2="250" y2="200" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                <line x1="150" y1="150" x2="150" y2="250" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                <line x1="150" y1="150" x2="50" y2="200" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                <line x1="150" y1="150" x2="50" y2="100" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                
                {/* AI評分結果填充區域 - 真正的六邊形 */}
                {showResult && (() => {
                  const scorePoints = getHexagonScorePoints(scores);
                  const pointsString = scorePoints.map(point => `${point.x},${point.y}`).join(' ');
                  
                  return (
                    <polygon 
                      key={`fill-${JSON.stringify(scores)}`}
                      points={pointsString}
                      fill="rgba(138, 43, 226, 0.6)" 
                      stroke="rgba(138, 43, 226, 0.8)" 
                      strokeWidth="2"
                    />
                  );
                })()}
                
                {/* 評分點 */}
                {showResult && (() => {
                  const scorePoints = getHexagonScorePoints(scores);
                  
                  return (
                    <>
                      {scorePoints.map((point, index) => (
                        <circle 
                          key={`point-${index}-${JSON.stringify(scores)}`}
                          cx={point.x} 
                          cy={point.y} 
                          r="4" 
                          fill="#ffd700" 
                        />
                      ))}
                    </>
                  );
                })()}
                
                {/* 軸線標籤 */}
                <text x="150" y="35" className="axis-label" textAnchor="middle">設計感 ({scores.love})</text>
                <text x="265" y="105" className="axis-label" textAnchor="start">愛情 ({scores.windfall})</text>
                <text x="265" y="205" className="axis-label" textAnchor="start">偏財 ({scores.regularIncome})</text>
                <text x="150" y="275" className="axis-label" textAnchor="middle">正財 ({scores.career})</text>
                <text x="35" y="205" className="axis-label" textAnchor="end">事業 ({scores.regularIncome})</text>
                <text x="35" y="105" className="axis-label" textAnchor="end">健康 ({scores.health})</text>
              </svg>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BeadRating;
