import React, { useState, useRef, useEffect } from 'react';
import './BeadCabinet.css';

// æµ®ç©ºç å­çµ„ä»¶
const FloatingBead = ({ drawer, drawerId, onClose, onDragStart, onDragEnd }) => {
  const [position, setPosition] = useState({ top: 0, left: 0 });
  const [isDragging, setIsDragging] = useState(false);

  useEffect(() => {
    const updatePosition = () => {
      const drawerElement = document.querySelector(`[data-drawer-id="${drawerId}"]`);
      if (drawerElement) {
        const rect = drawerElement.getBoundingClientRect();

        setPosition({
          top: rect.top - 150, // åœ¨æŠ½å±œä¸Šæ–¹150pxï¼Œçµ¦èªªæ˜è¦–çª—ç•™æ›´å¤šç©ºé–“
          left: rect.left + rect.width / 2
        });
      }
    };

    // ç«‹å³æ›´æ–°ä½ç½®
    updatePosition();

    // ç›£è½è¦–çª—å¤§å°è®ŠåŒ–
    window.addEventListener('resize', updatePosition);

    return () => {
      window.removeEventListener('resize', updatePosition);
    };
  }, [drawerId]);

  // æª¢æŸ¥æ˜¯å¦ç‚ºè–„è·ç¶ ç å­ï¼Œä½¿ç”¨æ“¬çœŸåœ–ç‰‡
  const isMintGreenBead = drawer.name === 'è–„è·ç¶ ';

  // èª¿è©¦è¨Šæ¯
  console.log('æµ®ç©ºç å­æª¢æŸ¥:', {
    drawerName: drawer.name,
    isMintGreen: isMintGreenBead,
    shouldUseImage: isMintGreenBead
  });

  return (
    <div 
      className="floating-bead-container"
      data-type={drawer.type}
      style={{
        top: `${position.top}px`,
        left: `${position.left}px`,
        transform: 'translateX(-50%)',
        zIndex: 2000,
        pointerEvents: 'none'
      }}
    >
      <div className="floating-bead-info" style={{ pointerEvents: 'auto' }}>
        <div className="floating-bead-text">
          <h4>{drawer.name}</h4>
          <small>{drawer.type}</small>
        </div>
        <button 
          className="close-floating-btn"
          onClick={onClose}
        >
          âœ•
        </button>
      </div>
      <div
        className={`floating-bead ${isDragging ? 'dragging' : ''} ${isMintGreenBead ? 'mint-green-floating-bead' : ''}`}
        data-type={drawer.type}
        draggable
        onClick={(e) => {
          e.stopPropagation();
          console.log('æµ®ç©ºç å­è¢«é»æ“Š:', drawer.name);
        }}
        onDragStart={(e) => {
          console.log('é–‹å§‹æ‹–æ›³æµ®ç©ºç å­:', drawer.name);
          setIsDragging(true);
          onDragStart(drawer, e);
        }}
        onDragEnd={(e) => {
          console.log('çµæŸæ‹–æ›³æµ®ç©ºç å­:', drawer.name);
          setIsDragging(false);
          onDragEnd(e);
        }}
        style={{
          position: 'absolute',
          top: '20%',
          left: '50%',
          transform: 'translateX(-50%)',
          zIndex: 2001,
          pointerEvents: 'auto',
          width: (drawer.type === 'éæ¸¡ç ') ? '15px' : 
                 (drawer.type === 'ç±³ç ' || drawer.type === 'çç ') ? '21px' : '60px',
          height: (drawer.type === 'éæ¸¡ç ') ? '15px' : 
                  (drawer.type === 'ç±³ç ' || drawer.type === 'çç ') ? '21px' : '60px'
        }}
        title={`æ‹–æ›³ ${drawer.name}`}
      >
        {/* æ‰€æœ‰ç å­éƒ½ä½¿ç”¨åœ–ç‰‡ */}
        <img 
          src={drawer.image} 
          alt={`${drawer.name}${drawer.type}`}
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'contain',
            borderRadius: '50%',
            outline: 'none',
            border: 'none',
            filter: 'none',
            boxShadow: 'none',
            textShadow: 'none'
          }}
          onError={(e) => {
            console.error(`${drawer.name}ç å­åœ–ç‰‡è¼‰å…¥å¤±æ•—:`, e);
            // å¦‚æœåœ–ç‰‡è¼‰å…¥å¤±æ•—ï¼Œå›é€€åˆ°åŸæœ‰æ¨£å¼
            e.target.style.display = 'none';
            // é¡¯ç¤ºå‚™ç”¨çš„é¡è‰²ç å­
            const fallbackBead = document.createElement('div');
            fallbackBead.style.cssText = `
              width: 100%;
              height: 100%;
              background-color: ${drawer.color};
              border-radius: 50%;
            `;
            e.target.parentNode.appendChild(fallbackBead);
          }}
          onLoad={() => {
            console.log(`${drawer.name}ç å­åœ–ç‰‡è¼‰å…¥æˆåŠŸ`);
          }}
        />
      </div>
    </div>
  );
};

// æœ¨è³ªä¸²ç ç›¤çµ„ä»¶
