  // 模擬AI分析過程
  const startAnalysis = () => {
    if (!savedDesign) {
      alert('請先在珠子收納櫃保存串珠計！');
      return;
    }

    setIsAnalyzing(true);
    setCurrentMessage('');
    setShowResult(false);

    // 模擬分析過程
    setTimeout(() => {
      // 根據實際珠子設計分析評分
      const newScores = analyzeBeadDesign(savedDesign);
      setScores(newScores);

      // 根據珠子設計生成個性化建議
      const personalizedAdvice = generatePersonalizedAdvice(savedDesign, newScores);
      setFullMessage(personalizedAdvice);

      // 逐字顯示建議
      let index = 0;
      const typeWriter = () => {
        if (index < personalizedAdvice.length) {
          setCurrentMessage(personalizedAdvice.substring(0, index + 1));
          index++;
          setTimeout(typeWriter, 50);
        } else {
          setShowResult(true);
          setIsAnalyzing(false);
        }
      };
      typeWriter();
    }, 2000);
  };

