  },
  
  seed: {
    name: 'Á±≥Áè†',
    description: 'Â∞èÂ∑ßÁ≤æÁ∑ªÔºåË≥™Âú∞Á¥∞ËÜ©',
    icon: '/white-bead-ID24.png',
    characteristics: [
      'Â∞∫ÂØ∏Â∞èÔºåÈÅ©ÂêàÁ≤æÁ¥∞Â∑•Ëóù',
      'Ë≥™Âú∞Â†ÖÁ°¨Ôºå‰∏çÊòìÊêçÂ£û',
      'Ëâ≤ÂΩ©ÊüîÂíåÔºåÈÅ©ÂêàÊê≠ÈÖç',
      'ÈáçÈáèËºïÔºå‰Ω©Êà¥ÁÑ°Ë≤†Êìî'
    ],
    care: 'ÈÅøÂÖçÊΩÆÊøïÔºåÂÆöÊúüÊì¶Êã≠',
    price: 'Á∂ìÊøüÂØ¶ÊÉ†',
    applications: ['Á≤æÁ¥∞Á∑®Áπî', 'Âà∫Áπ°Ë£ùÈ£æ', 'Â∞èÈÖç‰ª∂'],
    colors: ['Ê∑∫Á≤â', 'ËñÑËç∑Á∂†', 'Â§©Ëóç', 'Á±≥Ëâ≤', 'Ê∑∫Á¥´', 'Ê∑∫ÈªÉ']
  },
  
  crystal: {
    name: 'Ê∞¥Êô∂Áè†',
    description: 'È´òË≤¥ÂÖ∏ÈõÖÔºåÂÖâÊæ§ÁíÄÁí®',
    icon: '/dark-purple-bead-ID8.png',
    characteristics: [
      'ÊäòÂ∞ÑÁéáÈ´òÔºåÂÖâÊæ§ÁíÄÁí®',
      'Ë≥™Âú∞Â†ÖÁ°¨Ôºå‰∏çÊòìÂàÆÂÇ∑',
      'Ëâ≤ÂΩ©Á¥îÊ∑®ÔºåÈ´òË≤¥ÂÖ∏ÈõÖ',
      'ÈáçÈáèËºÉÈáçÔºåË≥™ÊÑüÂçÅË∂≥'
    ],
    care: 'ÈÅøÂÖçÂåñÂ≠∏ÂìÅÔºåÂÆöÊúüÊããÂÖâ',
    price: 'ËºÉÈ´ò',
    applications: ['È´òÁ¥öÈ¶ñÈ£æ', 'Á¶ÆÂìÅË£Ω‰Ωú', 'Êî∂ËóèÂìÅ'],
    colors: ['ÈÄèÊòé', 'Á≤âÊô∂', 'Á¥´Êô∂', 'ÈªÉÊô∂', 'Á∂†Êô∂', 'ÁôΩÊô∂']
  },
  
  wooden: {
    name: 'Êú®Áè†',
    description: 'Ëá™ÁÑ∂Ë≥™Ê®∏ÔºåÁí∞‰øùÂÅ•Â∫∑',
    icon: '/dark-brown-bead-ID17.png',
    characteristics: [
      'Â§©ÁÑ∂ÊùêË≥™ÔºåÁí∞‰øùÂÅ•Â∫∑',
      'Ë≥™Âú∞ËºïÁõàÔºå‰Ω©Êà¥ËàíÈÅ©',
      'Ëâ≤ÂΩ©Ëá™ÁÑ∂ÔºåË≥™Ê®∏Ê∫´È¶®',
      'ÂèØÈõïÂàªÔºåÂÄãÊÄßÂåñÂº∑'
    ],
    care: 'ÈÅøÂÖçÊ∞¥Êµ∏ÔºåÂÆöÊúü‰∏äÊ≤π',
