    hex: '#4ECDC4',
    recommendations: [
      {
        name: 'Â∞àÊ•≠ÊâãÈçä',
        length: '4/3Âúì',
        width: '‰∏≠Á≠â',
        description: 'ÈÅ©ÂêàÂ∑•‰ΩúÂ†¥ÂêàÔºåÂ±ïÁèæÂ∞àÊ•≠Ëàá‰ø°‰ªªÊÑü',
        beads: ['Ê∑∫ËóçÊ∞¥Êô∂Áè†', 'ÈäÄËâ≤ÈÅéÊ∏°Áè†', 'ÁôΩËâ≤Á±≥Áè†']
      },
      {
        name: '‰ºëÈñíÈ†ÖÈçä',
        length: 'ÂçäÂúì',
        width: 'Á¥∞Á∑ö',
