import React, { useState, useRef, useEffect } from 'react';
import './BeadRating.css';

const BeadRating = () => {
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [showResult, setShowResult] = useState(false);
  const [currentMessage, setCurrentMessage] = useState('');
  const [fullMessage, setFullMessage] = useState('');
  const [scores, setScores] = useState({
    love: 0,
    windfall: 0,
    regularIncome: 0,
    career: 0,
    health: 0
  });
  const [savedDesign, setSavedDesign] = useState(null);
  
  // Èõ∑ÈÅîÂúñËá™ÂÆöÁæ©ÈÅ∏È†Ö
  const [radarCustomization, setRadarCustomization] = useState({
    fillColor: '#8a2be2', // Á¥´Ëâ≤
    fillOpacity: 0.6,     // ÈÄèÊòéÂ∫¶
    strokeColor: '#8a2be2', // ÈÇäÊ°ÜÈ°èËâ≤
    strokeOpacity: 0.8,   // ÈÇäÊ°ÜÈÄèÊòéÂ∫¶
    strokeWidth: 2,       // ÈÇäÊ°ÜÂØ¨Â∫¶
    showGrid: true,       // È°ØÁ§∫Á∂≤Ê†º
    showAxis: true,       // È°ØÁ§∫Ëª∏Á∑ö
    showPoints: true,     // È°ØÁ§∫Ë©ïÂàÜÈªû
    showLabels: true      // È°ØÁ§∫Ê®ôÁ±§
  });

  // ÈÄöÈùàÂ∏´ÁöÑÂÆåÊï¥Âª∫Ë≠∞
