import React, { useState, useRef, useEffect } from 'react';
import './BeadRating.css';

const BeadRating = () => {
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [showResult, setShowResult] = useState(false);
  const [currentMessage, setCurrentMessage] = useState('');
  const [fullMessage, setFullMessage] = useState('');
  const [scores, setScores] = useState({
    love: 0,
    windfall: 0,
    regularIncome: 0,
    career: 0,
    health: 0
  });
  const [savedDesign, setSavedDesign] = useState(null);
  
  // 雷達圖自定義選項
  const [radarCustomization, setRadarCustomization] = useState({
    fillColor: '#8a2be2', // 紫色
    fillOpacity: 0.6,     // 透明度
    strokeColor: '#8a2be2', // 邊框顏色
    strokeOpacity: 0.8,   // 邊框透明度
    strokeWidth: 2,       // 邊框寬度
    showGrid: true,       // 顯示網格
    showAxis: true,       // 顯示軸線
    showPoints: true,     // 顯示評分點
    showLabels: true      // 顯示標籤
  });

  // 通靈師的完整建議
  const psychicAdvice = `你的串珠作品展現了獨特的藝術天賦和內在智慧。這件作品不僅是一件美麗的飾品，更是你內心世界的真實寫照。從愛情運勢來看，紅色珠子的點綴象徵著熱情與勇氣，預示著美好的愛情即將到來，在接下來的三個月內，你將遇到一位與你靈魂共鳴的人。在財富方面，藍色珠子的能量與水元素相呼應，預示著流動的財富即將到來，建議你保持開放的心態，留意身邊的投資機會，但切記不要過於貪心，穩健的理財方式會為你帶來意想不到的收穫。事業發展上，串珠的對稱排列反映了你對工作的認真態度，而金色珠子的點綴則象徵著豐厚的回報，你的努力將得到認可，升職加薪的機會就在眼前。從創造力角度來看，綠色珠子的能量與成長相呼應，預示著你的事業將進入快速發展期，新的項目機會將接踵而至。在健康方面，整體能量非常和諧，你的串珠作品散發著平衡與健康的氣息，紫色珠子的能量與靈性相呼應，預示著你的身心狀態將達到最佳狀態。建議你將這份創造力運用到生活的各個方面，相信自己的直覺，勇敢追求夢想，保持規律的作息，多接觸大自然。記住，每個珠子都承載著獨特的能量，就像你人生中的每個選擇都蘊含著無限可能。`;

  // 檢查是否有保存的串珠設計
  useEffect(() => {
    const savedDesignData = localStorage.getItem('savedBeadDesign');
    if (savedDesignData) {
      try {
        const design = JSON.parse(savedDesignData);
        setSavedDesign(design);
        console.log('載入保存的串珠設計:', design);
      } catch (error) {
        console.error('解析保存的設計數據失敗:', error);
      }
    }
  }, []);

  // 模擬AI分析過程
  const startAnalysis = () => {
    if (!savedDesign) {
      alert('請先在珠子收納櫃保存串珠設計！');
      return;
    }

    setIsAnalyzing(true);
    setCurrentMessage('');
    setShowResult(false);

    // 模擬分析過程
    setTimeout(() => {
      // 生成隨機評分
      const newScores = {
        love: Math.floor(Math.random() * 4) + 7, // 7-10分
        windfall: Math.floor(Math.random() * 4) + 6, // 6-9分
        regularIncome: Math.floor(Math.random() * 4) + 7, // 7-10分
        career: Math.floor(Math.random() * 4) + 6, // 6-9分
        health: Math.floor(Math.random() * 4) + 7 // 7-10分
      };
      setScores(newScores);

      // 逐字顯示建議
      let index = 0;
      const typeWriter = () => {
        if (index < psychicAdvice.length) {
          setCurrentMessage(psychicAdvice.substring(0, index + 1));
          index++;
          setTimeout(typeWriter, 50);
        } else {
          setShowResult(true);
          setIsAnalyzing(false);
        }
      };
      typeWriter();
    }, 2000);
  };

  // 清除保存的設計
  const clearSavedDesign = () => {
    localStorage.removeItem('savedBeadDesign');
    setSavedDesign(null);
    setShowResult(false);
    setCurrentMessage('');
    setFullMessage('');
  };

  // 前往珠子收納櫃創建新設計
  const goToBeadCabinet = () => {
    window.location.href = '/';
  };

  // 返回主頁
  const goHome = () => {
    window.open('http://127.0.0.1:5500/index.html', '_self');
  };



  return (
    <div className="bead-rating-container">
      {/* 返回按鈕 */}
      <div className="rating-header">
        <button className="back-btn" onClick={goHome}>
          🏠 返回主頁
        </button>
        <h1>⭐ 串珠評分區 ⭐</h1>
      </div>

      <div className="rating-content">
        {/* 左側：通靈師和對話框 */}
        <div className="left-panel">
          {/* 左上：對話框 */}
          <div className="dialogue-box">
            {!isAnalyzing && !showResult && (
              <div className="welcome-message">
                <p>歡迎來到神秘占卜殿堂，讓我來解讀你的串珠設計...</p>
                {savedDesign ? (
                  <p>我看到你精心設計的串珠作品，讓我為你揭示其中的奧秘。</p>
                ) : (
                  <p>請先在珠子收納櫃創建並保存您的串珠設計，我將為你揭示其中的奧秘。</p>
                )}
              </div>
            )}

            {isAnalyzing && (
              <div className="analyzing-message">
                <div className="crystal-animation">🔮</div>
                <p>我正在解讀你的串珠作品...</p>
                <p>水晶球正在揭示命運的奧秘...</p>
              </div>
            )}

            {showResult && (
              <div className="result-message">
                <p className="advice-text">{currentMessage}</p>
                {currentMessage.length < psychicAdvice.length && (
                  <div className="typing-indicator">|</div>
                )}
              </div>
            )}
          </div>

          {/* 左中下：AI虛擬通靈師 */}
          <div className="psychic-medium">
            <div className="psychic-avatar">
              <div className="psychic-character">
                <img 
                  src="/psychic-medium.png" 
                  alt="神秘通靈師" 
                  className="psychic-image"
                />
              </div>
              <div className="psychic-title">神秘通靈師</div>
              <div className="psychic-subtitle">✨ 能量分析專家 ✨</div>
            </div>
          </div>
        </div>

        {/* 右側：作品上傳和雷達圖 */}
        <div className="right-panel">
          {/* 串珠設計展示 */}
          <div className="bead-design-section">
            <h3>📿 你的串珠設計</h3>
            {savedDesign ? (
              <div className="design-display">
                <div className="design-info">
                  <h4>{savedDesign.designName}</h4>
                  <p>包含 {savedDesign.beads.length} 顆珠子</p>
                  <p>創建時間: {new Date(savedDesign.timestamp).toLocaleString()}</p>
                </div>
                
                {/* 串珠設計視覺化 */}
                <div className="bead-design-visualization">
                  <div className="bead-string-preview">
                    {savedDesign.beads.map((bead, index) => (
                      <div 
                        key={index}
                        className="design-bead"
                        style={{ 
                          backgroundColor: bead.color,
                          animationDelay: `${index * 0.1}s`
                        }}
                        title={`${bead.name} - ${bead.type}`}
                      >
                        <div className="bead-shine"></div>
                        <div className="bead-reflection"></div>
                        <div className="bead-glow" style={{ boxShadow: `0 0 15px ${bead.shine}` }}></div>
                      </div>
                    ))}
                  </div>
                </div>
                
                <div className="design-controls">
                  <button 
                    className="analyze-btn" 
                    onClick={startAnalysis}
                    disabled={isAnalyzing}
                  >
                    {isAnalyzing ? '🔮 分析中...' : '🔮 開始分析'}
                  </button>
                  <button 
                    className="clear-design-btn" 
                    onClick={clearSavedDesign}
                  >
                    🗑️ 清除設計
                  </button>
                </div>
              </div>
            ) : (
              <div className="no-design-message">
                <div className="no-design-icon">🎨</div>
                <h4>還沒有串珠設計</h4>
                <p>請先到珠子收納櫃創建並保存您的串珠設計</p>
                <button 
                  className="go-to-cabinet-btn" 
                  onClick={goToBeadCabinet}
                >
                  🏠 前往珠子收納櫃
                </button>
              </div>
            )}
          </div>

          {/* 雷達圖 */}
          <div className="radar-chart-section">
            <h3>🌟 能量評分圖</h3>
            <div className="radar-chart">
              {/* SVG雷達圖 */}
              <svg 
                className="radar-svg" 
                viewBox="0 0 300 300" 
                width="300" 
                height="300"
              >
                {/* 背景網格 - 三個同心六邊形 */}
                {radarCustomization.showGrid && (
                  <>
                    <polygon 
                      points="150,50 250,100 250,200 150,250 50,200 50,100" 
                      fill="none" 
                      stroke="rgba(255,255,255,0.3)" 
                      strokeWidth="1"
                    />
                    <polygon 
                      points="150,80 220,120 220,180 150,220 80,180 80,120" 
                      fill="none" 
                      stroke="rgba(255,255,255,0.3)" 
                      strokeWidth="1"
                    />
                    <polygon 
                      points="150,110 190,140 190,160 150,190 110,160 110,140" 
                      fill="none" 
                      stroke="rgba(255,255,255,0.3)" 
                      strokeWidth="1"
                    />
                  </>
                )}
                
                {/* 軸線 - 從中心到六個頂點 */}
                {radarCustomization.showAxis && (
                  <>
                    <line x1="150" y1="150" x2="150" y2="50" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                    <line x1="150" y1="150" x2="250" y2="100" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                    <line x1="150" y1="150" x2="250" y2="200" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                    <line x1="150" y1="150" x2="150" y2="250" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                    <line x1="150" y1="150" x2="50" y2="200" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                    <line x1="150" y1="150" x2="50" y2="100" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                  </>
                )}
                
                {/* 紫色填充區域 - 基於評分數據 */}
                {showResult && (
                  <polygon 
                    points={`
                      ${150 + (scores.love - 5) * 20},${50 + (scores.love - 5) * 20}
                      ${150 + (scores.windfall - 5) * 20},${100 + (scores.windfall - 5) * 20}
                      ${150 + (scores.regularIncome - 5) * 20},${200 + (scores.regularIncome - 5) * 20}
                      ${150 + (scores.career - 5) * 20},${250 + (scores.career - 5) * 20}
                      ${150 + (scores.health - 5) * 20},${200 + (scores.health - 5) * 20}
                      ${150 + (scores.love - 5) * 20},${100 + (scores.love - 5) * 20}
                    `}
                    fill={`${radarCustomization.fillColor}${Math.round(radarCustomization.fillOpacity * 255).toString(16).padStart(2, '0')}`}
                    stroke={`${radarCustomization.strokeColor}${Math.round(radarCustomization.strokeOpacity * 255).toString(16).padStart(2, '0')}`}
                    strokeWidth={radarCustomization.strokeWidth}
                  />
                )}
                
                {/* 評分點 */}
                {showResult && radarCustomization.showPoints && (
                  <>
                    <circle cx={150 + (scores.love - 5) * 20} cy={50 + (scores.love - 5) * 20} r="4" fill="#ffd700" />
                    <circle cx={150 + (scores.windfall - 5) * 20} cy={100 + (scores.windfall - 5) * 20} r="4" fill="#ffd700" />
                    <circle cx={150 + (scores.regularIncome - 5) * 20} cy={200 + (scores.regularIncome - 5) * 20} r="4" fill="#ffd700" />
                    <circle cx={150 + (scores.career - 5) * 20} cy={250 + (scores.career - 5) * 20} r="4" fill="#ffd700" />
                    <circle cx={150 + (scores.health - 5) * 20} cy={200 + (scores.health - 5) * 20} r="4" fill="#ffd700" />
                    <circle cx={150 + (scores.love - 5) * 20} cy={100 + (scores.love - 5) * 20} r="4" fill="#ffd700" />
                  </>
                )}
                
                {/* 軸線標籤 */}
                {radarCustomization.showLabels && (
                  <>
                    <text x="150" y="35" className="axis-label" textAnchor="middle">設計感</text>
                    <text x="265" y="105" className="axis-label" textAnchor="start">愛情</text>
                    <text x="265" y="205" className="axis-label" textAnchor="start">偏財</text>
                    <text x="150" y="275" className="axis-label" textAnchor="middle">正財</text>
                    <text x="35" y="205" className="axis-label" textAnchor="end">事業</text>
                    <text x="35" y="105" className="axis-label" textAnchor="end">健康</text>
                  </>
                )}
                
                {/* 評分標籤 */}
                {showResult && radarCustomization.showLabels && (
                  <>
                    <text x={150 + (scores.love - 5) * 20} y={50 + (scores.love - 5) * 20 - 10} className="score-label" textAnchor="middle">{scores.love}</text>
                    <text x={150 + (scores.windfall - 5) * 20 + 10} cy={100 + (scores.windfall - 5) * 20} className="score-label" textAnchor="middle">{scores.windfall}</text>
                    <text x={150 + (scores.regularIncome - 5) * 20 + 10} cy={200 + (scores.regularIncome - 5) * 20} className="score-label" textAnchor="middle">{scores.regularIncome}</text>
                    <text x={150 + (scores.career - 5) * 20} y={250 + (scores.career - 5) * 20 + 15} className="score-label" textAnchor="middle">{scores.career}</text>
                    <text x={150 + (scores.health - 5) * 20 - 10} cy={200 + (scores.health - 5) * 20} className="score-label" textAnchor="middle">{scores.health}</text>
                    <text x={150 + (scores.love - 5) * 20 - 10} cy={100 + (scores.love - 5) * 20} className="score-label" textAnchor="middle">{scores.love}</text>
                  </>
                )}
              </svg>
              
              {/* 雷達圖自定義控制選項 */}
              {showResult && (
                <div className="radar-customization-controls">
                  <h4>🎨 自定義雷達圖</h4>
                  <div className="customization-grid">
                    <div className="control-group">
                      <label>填充顏色:</label>
                      <input 
                        type="color" 
                        value={radarCustomization.fillColor}
                        onChange={(e) => setRadarCustomization(prev => ({...prev, fillColor: e.target.value}))}
                      />
                    </div>
                    <div className="control-group">
                      <label>填充透明度:</label>
                      <input 
                        type="range" 
                        min="0" 
                        max="1" 
                        step="0.1"
                        value={radarCustomization.fillOpacity}
                        onChange={(e) => setRadarCustomization(prev => ({...prev, fillOpacity: parseFloat(e.target.value)}))}
                      />
                      <span>{Math.round(radarCustomization.fillOpacity * 100)}%</span>
                    </div>
                    <div className="control-group">
                      <label>邊框顏色:</label>
                      <input 
                        type="color" 
                        value={radarCustomization.strokeColor}
                        onChange={(e) => setRadarCustomization(prev => ({...prev, strokeColor: e.target.value}))}
                      />
                    </div>
                    <div className="control-group">
                      <label>邊框寬度:</label>
                      <input 
                        type="range" 
                        min="1" 
                        max="5" 
                        step="0.5"
                        value={radarCustomization.strokeWidth}
                        onChange={(e) => setRadarCustomization(prev => ({...prev, strokeWidth: parseFloat(e.target.value)}))}
                      />
                      <span>{radarCustomization.strokeWidth}</span>
                    </div>
                    <div className="control-group">
                      <label>
                        <input 
                          type="checkbox" 
                          checked={radarCustomization.showGrid}
                          onChange={(e) => setRadarCustomization(prev => ({...prev, showGrid: e.target.checked}))}
                        />
                        顯示網格
                      </label>
                    </div>
                    <div className="control-group">
                      <label>
                        <input 
                          type="checkbox" 
                          checked={radarCustomization.showAxis}
                          onChange={(e) => setRadarCustomization(prev => ({...prev, showAxis: e.target.checked}))}
                        />
                        顯示軸線
                      </label>
                    </div>
                    <div className="control-group">
                      <label>
                        <input 
                          type="checkbox" 
                          checked={radarCustomization.showPoints}
                          onChange={(e) => setRadarCustomization(prev => ({...prev, showPoints: e.target.checked}))}
                        />
                        顯示評分點
                      </label>
                    </div>
                    <div className="control-group">
                      <label>
                        <input 
                          type="checkbox" 
                          checked={radarCustomization.showLabels}
                          onChange={(e) => setRadarCustomization(prev => ({...prev, showLabels: e.target.checked}))}
                        />
                        顯示標籤
                      </label>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BeadRating;
