import React, { useState, useRef, useEffect } from 'react';
import './BeadRating.css';

const BeadRating = () => {
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [showResult, setShowResult] = useState(false);
  const [currentMessage, setCurrentMessage] = useState('');
  const [fullMessage, setFullMessage] = useState('');
  const [scores, setScores] = useState({
    love: 0,
    windfall: 0,
    regularIncome: 0,
    career: 0,
    health: 0
  });
  const [savedDesign, setSavedDesign] = useState(null);

  // ÈÄöÈùàÂ∏´ÁöÑÂÆåÊï¥Âª∫Ë≠∞
  const psychicAdvice = `‰Ω†ÁöÑ‰∏≤Áè†‰ΩúÂìÅÂ±ïÁèæ‰∫ÜÁç®ÁâπÁöÑËóùË°ìÂ§©Ë≥¶ÂíåÂÖßÂú®Êô∫ÊÖß„ÄÇÈÄô‰ª∂‰ΩúÂìÅ‰∏çÂÉÖÊòØ‰∏Ä‰ª∂ÁæéÈ∫óÁöÑÈ£æÂìÅÔºåÊõ¥ÊòØ‰Ω†ÂÖßÂøÉ‰∏ñÁïåÁöÑÁúüÂØ¶ÂØ´ÁÖß„ÄÇÂæûÊÑõÊÉÖÈÅãÂã¢‰æÜÁúãÔºåÁ¥ÖËâ≤Áè†Â≠êÁöÑÈªûÁ∂¥Ë±°ÂæµËëóÁÜ±ÊÉÖËàáÂãáÊ∞£ÔºåÈ†êÁ§∫ËëóÁæéÂ•ΩÁöÑÊÑõÊÉÖÂç≥Â∞áÂà∞‰æÜÔºåÂú®Êé•‰∏ã‰æÜÁöÑ‰∏âÂÄãÊúàÂÖßÔºå‰Ω†Â∞áÈÅáÂà∞‰∏Ä‰ΩçËàá‰Ω†ÈùàÈ≠ÇÂÖ±È≥¥ÁöÑ‰∫∫„ÄÇÂú®Ë≤°ÂØåÊñπÈù¢ÔºåËóçËâ≤Áè†Â≠êÁöÑËÉΩÈáèËàáÊ∞¥ÂÖÉÁ¥†Áõ∏ÂëºÊáâÔºåÈ†êÁ§∫ËëóÊµÅÂãïÁöÑË≤°ÂØåÂç≥Â∞áÂà∞‰æÜÔºåÂª∫Ë≠∞‰Ω†‰øùÊåÅÈñãÊîæÁöÑÂøÉÊÖãÔºåÁïôÊÑèË∫´ÈÇäÁöÑÊäïË≥áÊ©üÊúÉÔºå‰ΩÜÂàáË®ò‰∏çË¶ÅÈÅéÊñºË≤™ÂøÉÔºåÁ©©ÂÅ•ÁöÑÁêÜË≤°ÊñπÂºèÊúÉÁÇ∫‰Ω†Â∏∂‰æÜÊÑèÊÉ≥‰∏çÂà∞ÁöÑÊî∂Á©´„ÄÇ‰∫ãÊ•≠ÁôºÂ±ï‰∏äÔºå‰∏≤Áè†ÁöÑÂ∞çÁ®±ÊéíÂàóÂèçÊò†‰∫Ü‰Ω†Â∞çÂ∑•‰ΩúÁöÑË™çÁúüÊÖãÂ∫¶ÔºåËÄåÈáëËâ≤Áè†Â≠êÁöÑÈªûÁ∂¥ÂâáË±°ÂæµËëóË±êÂéöÁöÑÂõûÂ†±Ôºå‰Ω†ÁöÑÂä™ÂäõÂ∞áÂæóÂà∞Ë™çÂèØÔºåÂçáËÅ∑Âä†Ëñ™ÁöÑÊ©üÊúÉÂ∞±Âú®ÁúºÂâç„ÄÇÂæûÂâµÈÄ†ÂäõËßíÂ∫¶‰æÜÁúãÔºåÁ∂†Ëâ≤Áè†Â≠êÁöÑËÉΩÈáèËàáÊàêÈï∑Áõ∏ÂëºÊáâÔºåÈ†êÁ§∫Ëëó‰Ω†ÁöÑ‰∫ãÊ•≠Â∞áÈÄ≤ÂÖ•Âø´ÈÄüÁôºÂ±ïÊúüÔºåÊñ∞ÁöÑÈ†ÖÁõÆÊ©üÊúÉÂ∞áÊé•Ë∏µËÄåËá≥„ÄÇÂú®ÂÅ•Â∫∑ÊñπÈù¢ÔºåÊï¥È´îËÉΩÈáèÈùûÂ∏∏ÂíåË´ßÔºå‰Ω†ÁöÑ‰∏≤Áè†‰ΩúÂìÅÊï£ÁôºËëóÂπ≥Ë°°ËàáÂÅ•Â∫∑ÁöÑÊ∞£ÊÅØÔºåÁ¥´Ëâ≤Áè†Â≠êÁöÑËÉΩÈáèËàáÈùàÊÄßÁõ∏ÂëºÊáâÔºåÈ†êÁ§∫Ëëó‰Ω†ÁöÑË∫´ÂøÉÁãÄÊÖãÂ∞áÈÅîÂà∞ÊúÄ‰Ω≥ÁãÄÊÖã„ÄÇÂª∫Ë≠∞‰Ω†Â∞áÈÄô‰ªΩÂâµÈÄ†ÂäõÈÅãÁî®Âà∞ÁîüÊ¥ªÁöÑÂêÑÂÄãÊñπÈù¢ÔºåÁõ∏‰ø°Ëá™Â∑±ÁöÑÁõ¥Ë¶∫ÔºåÂãáÊï¢ËøΩÊ±ÇÂ§¢ÊÉ≥Ôºå‰øùÊåÅË¶èÂæãÁöÑ‰ΩúÊÅØÔºåÂ§öÊé•Ëß∏Â§ßËá™ÁÑ∂„ÄÇË®ò‰ΩèÔºåÊØèÂÄãÁè†Â≠êÈÉΩÊâøËºâËëóÁç®ÁâπÁöÑËÉΩÈáèÔºåÂ∞±ÂÉè‰Ω†‰∫∫Áîü‰∏≠ÁöÑÊØèÂÄãÈÅ∏ÊìáÈÉΩËòäÂê´ËëóÁÑ°ÈôêÂèØËÉΩ„ÄÇ`;

  // Ê™¢Êü•ÊòØÂê¶Êúâ‰øùÂ≠òÁöÑ‰∏≤Áè†Ë®≠Ë®à
  useEffect(() => {
    const savedDesignData = localStorage.getItem('savedBeadDesign');
    if (savedDesignData) {
      try {
        const design = JSON.parse(savedDesignData);
        setSavedDesign(design);
        console.log('ËºâÂÖ•‰øùÂ≠òÁöÑ‰∏≤Áè†Ë®≠Ë®à:', design);
      } catch (error) {
        console.error('Ëß£Êûê‰øùÂ≠òÁöÑË®≠Ë®àÊï∏ÊìöÂ§±Êïó:', error);
      }
    }
  }, []);

  // Ê®°Êì¨AIÂàÜÊûêÈÅéÁ®ã
  const startAnalysis = () => {
    if (!savedDesign) {
      alert('Ë´ãÂÖàÂú®Áè†Â≠êÊî∂Á¥çÊ´É‰øùÂ≠ò‰∏≤Áè†Ë®≠Ë®àÔºÅ');
      return;
    }

    setIsAnalyzing(true);
    setCurrentMessage('');
    setShowResult(false);

    // Ê®°Êì¨ÂàÜÊûêÈÅéÁ®ã
    setTimeout(() => {
      // ÁîüÊàêÈö®Ê©üË©ïÂàÜ
      const newScores = {
        love: Math.floor(Math.random() * 4) + 7, // 7-10ÂàÜ
        windfall: Math.floor(Math.random() * 4) + 6, // 6-9ÂàÜ
        regularIncome: Math.floor(Math.random() * 4) + 7, // 7-10ÂàÜ
        career: Math.floor(Math.random() * 4) + 6, // 6-9ÂàÜ
        health: Math.floor(Math.random() * 4) + 7 // 7-10ÂàÜ
      };
      setScores(newScores);

      // ÈÄêÂ≠óÈ°ØÁ§∫Âª∫Ë≠∞
      let index = 0;
      const typeWriter = () => {
        if (index < psychicAdvice.length) {
          setCurrentMessage(psychicAdvice.substring(0, index + 1));
          index++;
          setTimeout(typeWriter, 50);
        } else {
          setShowResult(true);
          setIsAnalyzing(false);
        }
      };
      typeWriter();
    }, 2000);
  };

  // Ê∏ÖÈô§‰øùÂ≠òÁöÑË®≠Ë®à
  const clearSavedDesign = () => {
    localStorage.removeItem('savedBeadDesign');
    setSavedDesign(null);
    setShowResult(false);
    setCurrentMessage('');
    setFullMessage('');
  };

  // ÂâçÂæÄÁè†Â≠êÊî∂Á¥çÊ´ÉÂâµÂª∫Êñ∞Ë®≠Ë®à
  const goToBeadCabinet = () => {
    window.location.href = '/';
  };

  // ËøîÂõû‰∏ªÈ†Å
  const goHome = () => {
    window.open('http://127.0.0.1:5500/index.html', '_self');
  };

  return (
    <div className="bead-rating-container">
      {/* ËøîÂõûÊåâÈàï */}
      <div className="rating-header">
        <button className="back-btn" onClick={goHome}>
          üè† ËøîÂõû‰∏ªÈ†Å
        </button>
        <h1>‚≠ê ‰∏≤Áè†Ë©ïÂàÜÂçÄ ‚≠ê</h1>
      </div>

      <div className="rating-content">
        {/* Â∑¶ÂÅ¥ÔºöÈÄöÈùàÂ∏´ÂíåÂ∞çË©±Ê°Ü */}
        <div className="left-panel">
          {/* Â∑¶‰∏äÔºöÂ∞çË©±Ê°Ü */}
          <div className="dialogue-box">
            {!isAnalyzing && !showResult && (
              <div className="welcome-message">
                <p>Ê≠°Ëøé‰æÜÂà∞Á•ûÁßòÂç†ÂçúÊÆøÂ†ÇÔºåËÆìÊàë‰æÜËß£ËÆÄ‰Ω†ÁöÑ‰∏≤Áè†Ë®≠Ë®à...</p>
                {savedDesign ? (
                  <p>ÊàëÁúãÂà∞‰Ω†Á≤æÂøÉË®≠Ë®àÁöÑ‰∏≤Áè†‰ΩúÂìÅÔºåËÆìÊàëÁÇ∫‰Ω†Êè≠Á§∫ÂÖ∂‰∏≠ÁöÑÂ•ßÁßò„ÄÇ</p>
                ) : (
                  <p>Ë´ãÂÖàÂú®Áè†Â≠êÊî∂Á¥çÊ´ÉÂâµÂª∫‰∏¶‰øùÂ≠òÊÇ®ÁöÑ‰∏≤Áè†Ë®≠Ë®àÔºåÊàëÂ∞áÁÇ∫‰Ω†Êè≠Á§∫ÂÖ∂‰∏≠ÁöÑÂ•ßÁßò„ÄÇ</p>
                )}
              </div>
            )}

            {isAnalyzing && (
              <div className="analyzing-message">
                <div className="crystal-animation">üîÆ</div>
                <p>ÊàëÊ≠£Âú®Ëß£ËÆÄ‰Ω†ÁöÑ‰∏≤Áè†‰ΩúÂìÅ...</p>
                <p>Ê∞¥Êô∂ÁêÉÊ≠£Âú®Êè≠Á§∫ÂëΩÈÅãÁöÑÂ•ßÁßò...</p>
              </div>
            )}

            {showResult && (
              <div className="result-message">
                <p className="advice-text">{currentMessage}</p>
                {currentMessage.length < psychicAdvice.length && (
                  <div className="typing-indicator">|</div>
                )}
              </div>
            )}
          </div>

          {/* Â∑¶‰∏≠‰∏ãÔºöAIËôõÊì¨ÈÄöÈùàÂ∏´ */}
          <div className="psychic-medium">
            <div className="psychic-avatar">
              <div className="psychic-character">üßô‚Äç‚ôÄÔ∏è</div>
              <div className="psychic-body">
                <div className="psychic-robe">üëò</div>
                <div className="psychic-hands">ü§≤</div>
              </div>
            </div>
            <div className="psychic-title">Á•ûÁßòÈÄöÈùàÂ∏´</div>
            <div className="psychic-subtitle">üîÆ ÂëΩÈÅãËß£ËÆÄËÄÖ</div>
          </div>
        </div>

        {/* Âè≥ÂÅ¥Ôºö‰ΩúÂìÅ‰∏äÂÇ≥ÂíåÈõ∑ÈÅîÂúñ */}
        <div className="right-panel">
          {/* ‰∏≤Áè†Ë®≠Ë®àÂ±ïÁ§∫ */}
          <div className="bead-design-section">
            <h3>üìø ‰Ω†ÁöÑ‰∏≤Áè†Ë®≠Ë®à</h3>
            {savedDesign ? (
              <div className="design-display">
                <div className="design-info">
                  <h4>{savedDesign.designName}</h4>
                  <p>ÂåÖÂê´ {savedDesign.beads.length} È°ÜÁè†Â≠ê</p>
                  <p>ÂâµÂª∫ÊôÇÈñì: {new Date(savedDesign.timestamp).toLocaleString()}</p>
                </div>
                
                {/* ‰∏≤Áè†Ë®≠Ë®àË¶ñË¶∫Âåñ */}
                <div className="bead-design-visualization">
                  <div className="bead-string-preview">
                    {savedDesign.beads.map((bead, index) => (
                      <div 
                        key={index}
                        className="design-bead"
                        style={{ 
                          backgroundColor: bead.color,
                          animationDelay: `${index * 0.1}s`
                        }}
                        title={`${bead.name} - ${bead.type}`}
                      >
                        <div className="bead-shine"></div>
                        <div className="bead-reflection"></div>
                        <div className="bead-glow" style={{ boxShadow: `0 0 15px ${bead.shine}` }}></div>
                      </div>
                    ))}
                  </div>
                </div>
                
                <div className="design-controls">
                  <button 
                    className="analyze-btn" 
                    onClick={startAnalysis}
                    disabled={isAnalyzing}
                  >
                    {isAnalyzing ? 'üîÆ ÂàÜÊûê‰∏≠...' : 'üîÆ ÈñãÂßãÂàÜÊûê'}
                  </button>
                  <button 
                    className="clear-design-btn" 
                    onClick={clearSavedDesign}
                  >
                    üóëÔ∏è Ê∏ÖÈô§Ë®≠Ë®à
                  </button>
                </div>
              </div>
            ) : (
              <div className="no-design-message">
                <div className="no-design-icon">üé®</div>
                <h4>ÈÇÑÊ≤íÊúâ‰∏≤Áè†Ë®≠Ë®à</h4>
                <p>Ë´ãÂÖàÂà∞Áè†Â≠êÊî∂Á¥çÊ´ÉÂâµÂª∫‰∏¶‰øùÂ≠òÊÇ®ÁöÑ‰∏≤Áè†Ë®≠Ë®à</p>
                <button 
                  className="go-to-cabinet-btn" 
                  onClick={goToBeadCabinet}
                >
                  üè† ÂâçÂæÄÁè†Â≠êÊî∂Á¥çÊ´É
                </button>
              </div>
            )}
          </div>

          {/* Èõ∑ÈÅîÂúñ */}
          <div className="radar-chart-section">
            <h3>üåü ËÉΩÈáèË©ïÂàÜÂúñ</h3>
            <div className="radar-chart">
              <svg viewBox="0 0 200 200" className="radar-svg">
                {/* Ê≠£‰∫îÈÇäÂΩ¢Á∂≤Ê†º - 5ÂÄãÈ†ÇÈªû */}
                <polygon
                  points="100,20 150,50 150,150 100,180 50,150"
                  fill="none"
                  stroke="#ddd"
                  strokeWidth="1"
                />
                <polygon
                  points="100,40 140,60 140,140 100,160 60,140"
                  fill="none"
                  stroke="#ddd"
                  strokeWidth="1"
                />
                <polygon
                  points="100,60 130,70 130,130 100,140 70,130"
                  fill="none"
                  stroke="#ddd"
                  strokeWidth="1"
                />
                <polygon
                  points="100,80 120,80 120,120 100,120 80,120"
                  fill="none"
                  stroke="#ddd"
                  strokeWidth="1"
                />

                {/* Ë©ïÂàÜÂ°´ÂÖÖÂçÄÂüü - Ê≠£‰∫îÈÇäÂΩ¢ */}
                {showResult && (
                  <polygon
                    points={`100,${100 - scores.love * 8} ${100 + scores.windfall * 8},${100 - scores.windfall * 4} ${100 + scores.regularIncome * 8},${100 + scores.regularIncome * 4} ${100},${100 + scores.career * 8} ${100 - scores.health * 8},${100 - scores.health * 4}`}
                    fill="rgba(138, 43, 226, 0.3)"
                    stroke="rgba(138, 43, 226, 0.8)"
                    strokeWidth="2"
                  />
                )}

                {/* Ëª∏Á∑öÊ®ôÁ±§ - Ê≠£‰∫îÈÇäÂΩ¢‰∫îÂÄãÈ†ÇÈªû */}
                <text x="100" y="15" textAnchor="middle" className="axis-label">ÊÑõÊÉÖ</text>
                <text x="155" y="50" textAnchor="start" className="axis-label">ÂÅèË≤°ÈÅã</text>
                <text x="155" y="150" textAnchor="start" className="axis-label">Ê≠£Ë≤°ÈÅã</text>
                <text x="100" y="195" textAnchor="middle" className="axis-label">‰∫ãÊ•≠</text>
                <text x="45" y="150" textAnchor="end" className="axis-label">ÂÅ•Â∫∑</text>

                {/* ÂàÜÊï∏Ê®ôÁ§∫ */}
                {showResult && (
                  <>
                    <text x="100" y={100 - scores.love * 8 - 5} textAnchor="middle" className="score-label">{scores.love}</text>
                    <text x={100 + scores.windfall * 8 + 5} y={100 - scores.windfall * 4} textAnchor="start" className="score-label">{scores.windfall}</text>
                    <text x={100 + scores.regularIncome * 8 + 5} y={100 + scores.regularIncome * 4} textAnchor="start" className="score-label">{scores.regularIncome}</text>
                    <text x="100" y={100 + scores.career * 8 + 15} textAnchor="middle" className="score-label">{scores.career}</text>
                    <text x={100 - scores.health * 8 - 5} y={100 - scores.health * 4} textAnchor="end" className="score-label">{scores.health}</text>
                  </>
                )}
              </svg>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BeadRating;
