import React, { useState } from 'react';
import './BeadCabinet.css';

const BeadCabinet = () => {
  const [openDrawers, setOpenDrawers] = useState({});
  const [selectedDrawer, setSelectedDrawer] = useState(null);
  const [selectedBeads, setSelectedBeads] = useState([]);

  const toggleDrawer = (drawerId) => {
    setOpenDrawers(prev => ({
      ...prev,
      [drawerId]: !prev[drawerId]
    }));
    setSelectedDrawer(drawerId);
  };

  // ÂÆöÁæ©Áè†Â≠êÁöÑÈ°èËâ≤ÂíåÈ°ûÂûãÔºå‰ª•ÂèäÂ∞çÊáâÁöÑÂÖßÈÉ®È†ÅÈù¢
  const beadColors = [
    { id: 1, color: '#FF6B9D', name: 'Á≤âËâ≤', type: 'ÁéªÁíÉÁè†', page: '/tutorial' },
    { id: 2, color: '#4ECDC4', name: 'ËñÑËç∑Á∂†', type: 'ÁéªÁíÉÁè†', page: '/patterns' },
    { id: 3, color: '#FFE66D', name: 'ÈªÉËâ≤', type: 'ÁéªÁíÉÁè†', page: '/tutorial' },
    { id: 4, color: '#FF6B6B', name: 'Á¥ÖËâ≤', type: 'ÁéªÁíÉÁè†', page: '/patterns' },
    { id: 5, color: '#4ECDC4', name: 'Ê∑∫Ëóç', type: 'ÁéªÁíÉÁè†', page: '/tutorial' },
    { id: 6, color: '#FFA07A', name: 'Ê©òËâ≤', type: 'ÁéªÁíÉÁè†', page: '/patterns' },
    { id: 7, color: '#DDA0DD', name: 'Á¥´Ëâ≤', type: 'Á±≥Áè†', page: '/tutorial' },
    { id: 8, color: '#98FB98', name: 'Ê∑∫Á∂†', type: 'Á±≥Áè†', page: '/patterns' },
    { id: 9, color: '#F0E68C', name: 'Á±≥Ëâ≤', type: 'Á±≥Áè†', page: '/tutorial' },
    { id: 10, color: '#FFB6C1', name: 'Ê∑∫Á≤â', type: 'Á±≥Áè†', page: '/patterns' },
    { id: 11, color: '#87CEEB', name: 'Â§©Ëóç', type: 'Á±≥Áè†', page: '/tutorial' },
    { id: 12, color: '#DDA0DD', name: 'Ê∑°Á¥´', type: 'Á±≥Áè†', page: '/patterns' },
    { id: 13, color: '#F0E68C', name: 'ÈáëÊ£ï', type: 'Á±≥Áè†', page: '/tutorial' },
    { id: 14, color: '#4682B4', name: 'Ê∑±Ëóç', type: 'Á±≥Áè†', page: '/patterns' },
    { id: 15, color: '#228B22', name: 'Ê∑±Á∂†', type: 'Á±≥Áè†', page: '/tutorial' },
    { id: 16, color: '#DC143C', name: 'Ê∑±Á¥Ö', type: 'Á±≥Áè†', page: '/patterns' },
    { id: 17, color: '#40E0D0', name: 'ÈùíËâ≤', type: 'Á±≥Áè†', page: '/tutorial' },
    { id: 18, color: '#98FB98', name: 'ËñÑËç∑Á∂†', type: 'Á±≥Áè†', page: '/patterns' },
    { id: 19, color: '#FFD700', name: 'ÈáëËâ≤', type: 'ÁéªÁíÉÁè†', page: '/tutorial' },
    { id: 20, color: '#FF69B4', name: 'Ê∑±Á≤â', type: 'ÁéªÁíÉÁè†', page: '/patterns' },
    { id: 21, color: '#00CED1', name: 'Ê∑±Èùí', type: 'ÁéªÁíÉÁè†', page: '/tutorial' },
    { id: 22, color: '#FF6347', name: 'Áï™ËåÑÁ¥Ö', type: 'ÁéªÁíÉÁè†', page: '/patterns' },
    { id: 23, color: '#32CD32', name: 'ËêäÂßÜÁ∂†', type: 'ÁéªÁíÉÁè†', page: '/tutorial' },
    { id: 24, color: '#9370DB', name: '‰∏≠Á¥´', type: 'ÁéªÁíÉÁè†', page: '/patterns' },
    { id: 25, color: '#FF4500', name: 'Ê©ôÁ¥Ö', type: 'ÁéªÁíÉÁè†', page: '/tutorial' }
  ];

  // ÂâµÂª∫ÊäΩÂ±úÈô£Âàó - Â∑¶ÂÅ¥5Â±§3Ê†ºÔºåÂè≥ÂÅ¥5Â±§2Ê†º
  const leftDrawers = [];
  const rightDrawers = [];

  // Â∑¶ÂÅ¥ÊäΩÂ±ú (5Â±§ x 3Ê†º = 15ÂÄã)
  for (let row = 0; row < 5; row++) {
    for (let col = 0; col < 3; col++) {
      const index = row * 3 + col;
      if (index < beadColors.length) {
        leftDrawers.push({
          id: `left-${row}-${col}`,
          ...beadColors[index]
        });
      }
    }
  }

  // Âè≥ÂÅ¥ÊäΩÂ±ú (5Â±§ x 2Ê†º = 10ÂÄã)
  for (let row = 0; row < 5; row++) {
    for (let col = 0; col < 2; col++) {
      const index = 15 + row * 2 + col;
      if (index < beadColors.length) {
        rightDrawers.push({
          id: `right-${row}-${col}`,
          ...beadColors[index]
        });
      }
    }
  }

  // ËôïÁêÜÂÖßÈÉ®È†ÅÈù¢Â∞éËà™
  const handleInternalNavigation = (page, event) => {
    event.stopPropagation();
    window.location.href = page;
  };

  // ËôïÁêÜË©≥Á¥∞Ë≥áË®ä
  const handleDetailInfo = (drawerId, event) => {
    event.stopPropagation();
    console.log(`Êü•ÁúãÊäΩÂ±ú ${drawerId} ÁöÑË©≥Á¥∞Ë≥áË®ä`);
  };

  // Ê∑ªÂä†Áè†Â≠êÂà∞‰∏≤Áè†Áõ§
  const addBeadToTray = (bead) => {
    setSelectedBeads(prev => [...prev, { ...bead, id: Date.now() }]);
  };

  // Âæû‰∏≤Áè†Áõ§ÁßªÈô§Áè†Â≠ê
  const removeBeadFromTray = (beadId) => {
    setSelectedBeads(prev => prev.filter(bead => bead.id !== beadId));
  };

  // Ê∏ÖÁ©∫‰∏≤Áè†Áõ§
  const clearTray = () => {
    setSelectedBeads([]);
  };

  return (
    <div className="bead-cabinet-container">
      <div className="cabinet-title">
        <h1>‚ú® Áè†Â≠êÊî∂Á¥çÊ´É ‚ú®</h1>
        <p>ÈªûÊìäÊäΩÂ±úÊü•Áúã‰∏çÂêåÈ°èËâ≤ÁöÑÁè†Â≠êÔºå‰∏¶ÈÄ£Êé•Âà∞Áõ∏ÈóúÈ†ÅÈù¢</p>
      </div>
      
      <div className="main-content">
        {/* Â∑¶ÂçäÈÇä - Êî∂Á¥çÊ´É */}
        <div className="cabinet-section">
          <div className="bead-cabinet">
            {/* Â∑¶ÂÅ¥Êî∂Á¥çÊ´É */}
            <div className="cabinet-unit left-unit">
              <div className="cabinet-frame">
                {leftDrawers.map((drawer) => (
                  <div key={drawer.id} className="drawer-container">
                    <div 
                      className={`drawer ${openDrawers[drawer.id] ? 'open' : ''}`}
                      onClick={() => toggleDrawer(drawer.id)}
                    >
                      <div className="drawer-front">
                        <div className="drawer-handle"></div>
                        <div className="bead-preview">
                          <div 
                            className="bead-sample" 
                            style={{ backgroundColor: drawer.color }}
                          ></div>
                        </div>
                      </div>
                      <div className="drawer-content">
                        <div className="bead-info">
                          <h3>{drawer.name}</h3>
                          <p>{drawer.type}</p>
                          <div className="bead-pile">
                            {[...Array(12)].map((_, i) => (
                              <div 
                                key={i}
                                className="bead"
                                style={{ 
                                  backgroundColor: drawer.color,
                                  left: `${Math.random() * 60}%`,
                                  top: `${Math.random() * 60}%`,
                                  animationDelay: `${i * 0.1}s`
                                }}
                              ></div>
                            ))}
                          </div>
                          <div className="drawer-links">
                            <button 
                              className="link-btn external-link"
                              onClick={(e) => handleInternalNavigation(drawer.page, e)}
                            >
                              üìñ Áõ∏ÈóúÈ†ÅÈù¢
                            </button>
                            <button 
                              className="link-btn add-bead-btn"
                              onClick={(e) => {
                                e.stopPropagation();
                                addBeadToTray(drawer);
                              }}
                            >
                              ‚ûï Âä†ÂÖ•‰∏≤Áè†Áõ§
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Âè≥ÂÅ¥Êî∂Á¥çÊ´É */}
            <div className="cabinet-unit right-unit">
              <div className="cabinet-frame">
                {rightDrawers.map((drawer) => (
                  <div key={drawer.id} className="drawer-container">
                    <div 
                      className={`drawer ${openDrawers[drawer.id] ? 'open' : ''}`}
                      onClick={() => toggleDrawer(drawer.id)}
                    >
                      <div className="drawer-front">
                        <div className="drawer-handle"></div>
                        <div className="bead-preview">
                          <div 
                            className="bead-sample" 
                            style={{ backgroundColor: drawer.color }}
                          ></div>
                        </div>
                      </div>
                      <div className="drawer-content">
                        <div className="bead-info">
                          <h3>{drawer.name}</h3>
                          <p>{drawer.type}</p>
                          <div className="bead-pile">
                            {[...Array(12)].map((_, i) => (
                              <div 
                                key={i}
                                className="bead"
                                style={{ 
                                  backgroundColor: drawer.color,
                                  left: `${Math.random() * 60}%`,
                                  top: `${Math.random() * 60}%`,
                                  animationDelay: `${i * 0.1}s`
                                }}
                              ></div>
                            ))}
                          </div>
                          <div className="drawer-links">
                            <button 
                              className="link-btn external-link"
                              onClick={(e) => handleInternalNavigation(drawer.page, e)}
                            >
                              üìñ Áõ∏ÈóúÈ†ÅÈù¢
                            </button>
                            <button 
                              className="link-btn add-bead-btn"
                              onClick={(e) => {
                                e.stopPropagation();
                                addBeadToTray(drawer);
                              }}
                            >
                              ‚ûï Âä†ÂÖ•‰∏≤Áè†Áõ§
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          <div className="cabinet-controls">
            <button 
              className="control-btn"
              onClick={() => setOpenDrawers({})}
            >
              ÈóúÈñâÊâÄÊúâÊäΩÂ±ú
            </button>
            <button 
              className="control-btn"
              onClick={() => {
                const allDrawers = {};
                [...leftDrawers, ...rightDrawers].forEach(drawer => {
                  allDrawers[drawer.id] = true;
                });
                setOpenDrawers(allDrawers);
              }}
            >
              ÊâìÈñãÊâÄÊúâÊäΩÂ±ú
            </button>
            <button 
              className="control-btn"
              onClick={() => window.location.href = '/tutorial'}
            >
              üìö ÊïôÂ≠∏‰∏≠ÂøÉ
            </button>
            <button 
              className="control-btn"
              onClick={() => window.location.href = '/patterns'}
            >
              üìö ÂúñÊ°àÂ∫´
            </button>
          </div>
        </div>

        {/* Âè≥ÂçäÈÇä - ‰∏≤Áè†Áõ§ */}
        <div className="bead-tray-section">
          <div className="bead-tray">
            <div className="tray-header">
              <h2>üé® ‰∏≤Áè†Ââµ‰ΩúÁõ§</h2>
              <p>Â∞áÂñúÊ≠°ÁöÑÁè†Â≠êÂä†ÂÖ•Ââµ‰ΩúÁõ§ÔºåË®≠Ë®à‰Ω†ÁöÑ‰ΩúÂìÅ</p>
            </div>
            
            <div className="tray-content">
              <div className="tray-grooves">
                {[...Array(8)].map((_, index) => (
                  <div key={index} className="tray-groove">
                    {selectedBeads
                      .filter((_, beadIndex) => beadIndex % 8 === index)
                      .map((bead) => (
                        <div
                          key={bead.id}
                          className="tray-bead"
                          style={{ backgroundColor: bead.color }}
                          onClick={() => removeBeadFromTray(bead.id)}
                          title={`${bead.name} - ÈªûÊìäÁßªÈô§`}
                        >
                          <span className="bead-name">{bead.name}</span>
                        </div>
                      ))}
                  </div>
                ))}
              </div>
              
              <div className="tray-controls">
                <button 
                  className="tray-btn clear-btn"
                  onClick={clearTray}
                >
                  üóëÔ∏è Ê∏ÖÁ©∫‰∏≤Áè†Áõ§
                </button>
                <button 
                  className="tray-btn save-btn"
                  onClick={() => alert('üíæ ‰ΩúÂìÅÂ∑≤ÂÑ≤Â≠òÔºÅ')}
                >
                  üíæ ÂÑ≤Â≠ò‰ΩúÂìÅ
                </button>
                <button 
                  className="tray-btn preview-btn"
                  onClick={() => alert('üëÅÔ∏è È†êË¶Ω‰ΩúÂìÅÂäüËÉΩÈñãÁôº‰∏≠')}
                >
                  üëÅÔ∏è È†êË¶Ω‰ΩúÂìÅ
                </button>
              </div>
              
              <div className="tray-info">
                <p>Â∑≤ÈÅ∏Êìá {selectedBeads.length} È°ÜÁè†Â≠ê</p>
                <p>ÈªûÊìäÁè†Â≠êÂèØÁßªÈô§ÔºåÈªûÊìä„Äå‚ûï Âä†ÂÖ•‰∏≤Áè†Áõ§„ÄçÂèØÊ∑ªÂä†Áè†Â≠ê</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BeadCabinet;
