import React, { useState } from 'react';
import './BeadCabinet.css';

const BeadCabinet = () => {
  const [openDrawers, setOpenDrawers] = useState({});
  const [selectedDrawer, setSelectedDrawer] = useState(null);
  const [selectedBeads, setSelectedBeads] = useState([]);

  const toggleDrawer = (drawerId) => {
    setOpenDrawers(prev => ({
      ...prev,
      [drawerId]: !prev[drawerId]
    }));
    setSelectedDrawer(drawerId);
  };

  // 定義珠子的顏色和類型，以及對應的內部頁面
  const beadColors = [
    { id: 1, color: '#FF6B9D', name: '粉色', type: '玻璃珠', page: '/tutorial' },
    { id: 2, color: '#4ECDC4', name: '薄荷綠', type: '玻璃珠', page: '/patterns' },
    { id: 3, color: '#FFE66D', name: '黃色', type: '玻璃珠', page: '/tutorial' },
    { id: 4, color: '#FF6B6B', name: '紅色', type: '玻璃珠', page: '/patterns' },
    { id: 5, color: '#4ECDC4', name: '淺藍', type: '玻璃珠', page: '/tutorial' },
    { id: 6, color: '#FFA07A', name: '橘色', type: '玻璃珠', page: '/patterns' },
    { id: 7, color: '#DDA0DD', name: '紫色', type: '米珠', page: '/tutorial' },
    { id: 8, color: '#98FB98', name: '淺綠', type: '米珠', page: '/patterns' },
    { id: 9, color: '#F0E68C', name: '米色', type: '米珠', page: '/tutorial' },
    { id: 10, color: '#FFB6C1', name: '淺粉', type: '米珠', page: '/patterns' },
    { id: 11, color: '#87CEEB', name: '天藍', type: '米珠', page: '/tutorial' },
    { id: 12, color: '#DDA0DD', name: '淡紫', type: '米珠', page: '/patterns' },
    { id: 13, color: '#F0E68C', name: '金棕', type: '米珠', page: '/tutorial' },
    { id: 14, color: '#4682B4', name: '深藍', type: '米珠', page: '/patterns' },
    { id: 15, color: '#228B22', name: '深綠', type: '米珠', page: '/tutorial' },
    { id: 16, color: '#DC143C', name: '深紅', type: '米珠', page: '/patterns' },
    { id: 17, color: '#40E0D0', name: '青色', type: '米珠', page: '/tutorial' },
    { id: 18, color: '#98FB98', name: '薄荷綠', type: '米珠', page: '/patterns' },
    { id: 19, color: '#FFD700', name: '金色', type: '玻璃珠', page: '/tutorial' },
    { id: 20, color: '#FF69B4', name: '深粉', type: '玻璃珠', page: '/patterns' },
    { id: 21, color: '#00CED1', name: '深青', type: '玻璃珠', page: '/tutorial' },
    { id: 22, color: '#FF6347', name: '番茄紅', type: '玻璃珠', page: '/patterns' },
    { id: 23, color: '#32CD32', name: '萊姆綠', type: '玻璃珠', page: '/tutorial' },
    { id: 24, color: '#9370DB', name: '中紫', type: '玻璃珠', page: '/patterns' },
    { id: 25, color: '#FF4500', name: '橙紅', type: '玻璃珠', page: '/tutorial' }
  ];

  // 創建抽屜陣列 - 左側5層3格，右側5層2格
  const leftDrawers = [];
  const rightDrawers = [];

  // 左側抽屜 (5層 x 3格 = 15個)
  for (let row = 0; row < 5; row++) {
    for (let col = 0; col < 3; col++) {
      const index = row * 3 + col;
      if (index < beadColors.length) {
        leftDrawers.push({
          id: `left-${row}-${col}`,
          ...beadColors[index]
        });
      }
    }
  }

  // 右側抽屜 (5層 x 2格 = 10個)
  for (let row = 0; row < 5; row++) {
    for (let col = 0; col < 2; col++) {
      const index = 15 + row * 2 + col;
      if (index < beadColors.length) {
        rightDrawers.push({
          id: `right-${row}-${col}`,
          ...beadColors[index]
        });
      }
    }
  }

  // 處理內部頁面導航
  const handleInternalNavigation = (page, event) => {
    event.stopPropagation();
    window.location.href = page;
  };

  // 處理詳細資訊
  const handleDetailInfo = (drawerId, event) => {
    event.stopPropagation();
    console.log(`查看抽屜 ${drawerId} 的詳細資訊`);
  };

  // 添加珠子到串珠盤
  const addBeadToTray = (bead) => {
    setSelectedBeads(prev => [...prev, { ...bead, id: Date.now() }]);
  };

  // 從串珠盤移除珠子
  const removeBeadFromTray = (beadId) => {
    setSelectedBeads(prev => prev.filter(bead => bead.id !== beadId));
  };

  // 清空串珠盤
  const clearTray = () => {
    setSelectedBeads([]);
  };

  return (
    <div className="bead-cabinet-container">
      <div className="cabinet-title">
        <h1>✨ 珠子收納櫃 ✨</h1>
        <p>點擊抽屜查看不同顏色的珠子，並連接到相關頁面</p>
      </div>
      
      <div className="main-content">
        {/* 左半邊 - 收納櫃 */}
        <div className="cabinet-section">
          <div className="bead-cabinet">
            {/* 左側收納櫃 */}
            <div className="cabinet-unit left-unit">
              <div className="cabinet-frame">
                {leftDrawers.map((drawer) => (
                  <div key={drawer.id} className="drawer-container">
                    <div 
                      className={`drawer ${openDrawers[drawer.id] ? 'open' : ''}`}
                      onClick={() => toggleDrawer(drawer.id)}
                    >
                      <div className="drawer-front">
                        <div className="drawer-handle"></div>
                        <div className="bead-preview">
                          <div 
                            className="bead-sample" 
                            style={{ backgroundColor: drawer.color }}
                          ></div>
                        </div>
                      </div>
                      <div className="drawer-content">
                        <div className="bead-info">
                          <h3>{drawer.name}</h3>
                          <p>{drawer.type}</p>
                          <div className="bead-pile">
                            {[...Array(12)].map((_, i) => (
                              <div 
                                key={i}
                                className="bead"
                                style={{ 
                                  backgroundColor: drawer.color,
                                  left: `${Math.random() * 60}%`,
                                  top: `${Math.random() * 60}%`,
                                  animationDelay: `${i * 0.1}s`
                                }}
                              ></div>
                            ))}
                          </div>
                          <div className="drawer-links">
                            <button 
                              className="link-btn external-link"
                              onClick={(e) => handleInternalNavigation(drawer.page, e)}
                            >
                              📖 相關頁面
                            </button>
                            <button 
                              className="link-btn add-bead-btn"
                              onClick={(e) => {
                                e.stopPropagation();
                                addBeadToTray(drawer);
                              }}
                            >
                              ➕ 加入串珠盤
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* 右側收納櫃 */}
            <div className="cabinet-unit right-unit">
              <div className="cabinet-frame">
                {rightDrawers.map((drawer) => (
                  <div key={drawer.id} className="drawer-container">
                    <div 
                      className={`drawer ${openDrawers[drawer.id] ? 'open' : ''}`}
                      onClick={() => toggleDrawer(drawer.id)}
                    >
                      <div className="drawer-front">
                        <div className="drawer-handle"></div>
                        <div className="bead-preview">
                          <div 
                            className="bead-sample" 
                            style={{ backgroundColor: drawer.color }}
                          ></div>
                        </div>
                      </div>
                      <div className="drawer-content">
                        <div className="bead-info">
                          <h3>{drawer.name}</h3>
                          <p>{drawer.type}</p>
                          <div className="bead-pile">
                            {[...Array(12)].map((_, i) => (
                              <div 
                                key={i}
                                className="bead"
                                style={{ 
                                  backgroundColor: drawer.color,
                                  left: `${Math.random() * 60}%`,
                                  top: `${Math.random() * 60}%`,
                                  animationDelay: `${i * 0.1}s`
                                }}
                              ></div>
                            ))}
                          </div>
                          <div className="drawer-links">
                            <button 
                              className="link-btn external-link"
                              onClick={(e) => handleInternalNavigation(drawer.page, e)}
                            >
                              📖 相關頁面
                            </button>
                            <button 
                              className="link-btn add-bead-btn"
                              onClick={(e) => {
                                e.stopPropagation();
                                addBeadToTray(drawer);
                              }}
                            >
                              ➕ 加入串珠盤
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          <div className="cabinet-controls">
            <button 
              className="control-btn"
              onClick={() => setOpenDrawers({})}
            >
              關閉所有抽屜
            </button>
            <button 
              className="control-btn"
              onClick={() => {
                const allDrawers = {};
                [...leftDrawers, ...rightDrawers].forEach(drawer => {
                  allDrawers[drawer.id] = true;
                });
                setOpenDrawers(allDrawers);
              }}
            >
              打開所有抽屜
            </button>
            <button 
              className="control-btn"
              onClick={() => window.location.href = '/tutorial'}
            >
              📚 教學中心
            </button>
            <button 
              className="control-btn"
              onClick={() => window.location.href = '/patterns'}
            >
              📚 圖案庫
            </button>
          </div>
        </div>

        {/* 右半邊 - 串珠盤 */}
        <div className="bead-tray-section">
          <div className="bead-tray">
            <div className="tray-header">
              <h2>🎨 串珠創作盤</h2>
              <p>將喜歡的珠子加入創作盤，設計你的作品</p>
            </div>
            
            <div className="tray-content">
              <div className="tray-grooves">
                {[...Array(8)].map((_, index) => (
                  <div key={index} className="tray-groove">
                    {selectedBeads
                      .filter((_, beadIndex) => beadIndex % 8 === index)
                      .map((bead) => (
                        <div
                          key={bead.id}
                          className="tray-bead"
                          style={{ backgroundColor: bead.color }}
                          onClick={() => removeBeadFromTray(bead.id)}
                          title={`${bead.name} - 點擊移除`}
                        >
                          <span className="bead-name">{bead.name}</span>
                        </div>
                      ))}
                  </div>
                ))}
              </div>
              
              <div className="tray-controls">
                <button 
                  className="tray-btn clear-btn"
                  onClick={clearTray}
                >
                  🗑️ 清空串珠盤
                </button>
                <button 
                  className="tray-btn save-btn"
                  onClick={() => alert('💾 作品已儲存！')}
                >
                  💾 儲存作品
                </button>
                <button 
                  className="tray-btn preview-btn"
                  onClick={() => alert('👁️ 預覽作品功能開發中')}
                >
                  👁️ 預覽作品
                </button>
              </div>
              
              <div className="tray-info">
                <p>已選擇 {selectedBeads.length} 顆珠子</p>
                <p>點擊珠子可移除，點擊「➕ 加入串珠盤」可添加珠子</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BeadCabinet;
