import React, { useState, useRef, useEffect } from 'react';
import './BeadRating.css';

const BeadRating = () => {
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [showResult, setShowResult] = useState(false);
  const [currentMessage, setCurrentMessage] = useState('');
  const [fullMessage, setFullMessage] = useState('');
  const [scores, setScores] = useState({
    love: 0,
    windfall: 0,
    regularIncome: 0,
    career: 0,
    health: 0
  });
  const [savedDesign, setSavedDesign] = useState(null);
  const [showDesignPreview, setShowDesignPreview] = useState(false);

  // 通靈師的完整建議
  const psychicAdvice = `你的串珠作品展現了獨特的藝術天賦和內在智慧。這件作品不僅是一件美麗的飾品，更是你內心世界的真實寫照。從愛情運勢來看，紅色珠子的點綴象徵著熱情與勇氣，預示著美好的愛情即將到來，在接下來的三個月內，你將遇到一位與你靈魂共鳴的人。在財富方面，藍色珠子的能量與水元素相呼應，預示著流動的財富即將到來，建議你保持開放的心態，留意身邊的投資機會，但切記不要過於貪心，穩健的理財方式會為你帶來意想不到的收穫。事業發展上，串珠的對稱排列反映了你對工作的認真態度，而金色珠子的點綴則象徵著豐厚的回報，你的努力將得到認可，升職加薪的機會就在眼前。從創造力角度來看，綠色珠子的能量與成長相呼應，預示著你的事業將進入快速發展期，新的項目機會將接踵而至。在健康方面，整體能量非常和諧，你的串珠作品散發著平衡與健康的氣息，紫色珠子的能量與靈性相呼應，預示著你的身心狀態將達到最佳狀態。建議你將這份創造力運用到生活的各個方面，相信自己的直覺，勇敢追求夢想，保持規律的作息，多接觸大自然。記住，每個珠子都承載著獨特的能量，就像你人生中的每個選擇都蘊含著無限可能。`;

  // 檢查是否有保存的串珠設計
  useEffect(() => {
    const savedDesignData = localStorage.getItem('savedBeadDesign');
    if (savedDesignData) {
      try {
        const design = JSON.parse(savedDesignData);
        setSavedDesign(design);
        console.log('載入保存的串珠設計:', design);
      } catch (error) {
        console.error('解析保存的設計數據失敗:', error);
      }
    }
  }, []);

  // 模擬AI分析過程
  const startAnalysis = () => {
    if (!savedDesign) {
      alert('請先在珠子收納櫃保存串珠設計！');
      return;
    }

    setIsAnalyzing(true);
    setCurrentMessage('');
    setShowResult(false);

    // 模擬分析過程
    setTimeout(() => {
      // 生成隨機評分
      const newScores = {
        love: Math.floor(Math.random() * 4) + 7, // 7-10分
        windfall: Math.floor(Math.random() * 4) + 6, // 6-9分
        regularIncome: Math.floor(Math.random() * 4) + 7, // 7-10分
        career: Math.floor(Math.random() * 4) + 6, // 6-9分
        health: Math.floor(Math.random() * 4) + 7 // 7-10分
      };
      setScores(newScores);

      // 逐字顯示建議
      let index = 0;
      const typeWriter = () => {
        if (index < psychicAdvice.length) {
          setCurrentMessage(psychicAdvice.substring(0, index + 1));
          index++;
          setTimeout(typeWriter, 50);
        } else {
          setShowResult(true);
          setIsAnalyzing(false);
        }
      };
      typeWriter();
    }, 2000);
  };

  // 處理圖片上傳
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setUploadedImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // 觸發文件選擇
  const triggerFileSelect = () => {
    fileInputRef.current.click();
  };

  // 處理拖拽放置
  const [isDragOver, setIsDragOver] = useState(false);

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setIsDragOver(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragOver(false);
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      if (file.type.startsWith('image/')) {
        const reader = new FileReader();
        reader.onload = (e) => {
          setUploadedImage(e.target.result);
        };
        reader.readAsDataURL(file);
      } else {
        alert('請上傳圖片文件！');
      }
    }
  };

  // 返回主頁
  const goHome = () => {
    window.open('http://127.0.0.1:5500/index.html', '_self');
  };

  return (
    <div className="bead-rating-container">
      {/* 返回按鈕 */}
      <div className="rating-header">
        <button className="back-btn" onClick={goHome}>
          🏠 返回主頁
        </button>
        <h1>⭐ 串珠評分區 ⭐</h1>
      </div>

      <div className="rating-content">
        {/* 左側：通靈師和對話框 */}
        <div className="left-panel">
          {/* AI虛擬通靈師 */}
          <div className="psychic-medium">
            <div className="psychic-avatar">
              <div className="crystal-ball">🔮</div>
              <div className="psychic-character">👩‍🔮</div>
            </div>
            <div className="psychic-title">神秘通靈師</div>
          </div>

          {/* 對話框 */}
          <div className="dialogue-box">
            {!isAnalyzing && !showResult && (
              <div className="welcome-message">
                <p>歡迎來到神秘占卜殿堂，讓我來解讀你的串珠作品...</p>
                <p>請上傳你的作品，我將為你揭示其中的奧秘。</p>
              </div>
            )}

            {isAnalyzing && (
              <div className="analyzing-message">
                <div className="crystal-animation">🔮</div>
                <p>我正在解讀你的串珠作品...</p>
                <p>水晶球正在揭示命運的奧秘...</p>
              </div>
            )}

            {showResult && (
              <div className="result-message">
                <p className="advice-text">{currentMessage}</p>
                {currentMessage.length < psychicAdvice.length && (
                  <div className="typing-indicator">|</div>
                )}
              </div>
            )}
          </div>
        </div>

        {/* 右側：作品上傳和雷達圖 */}
        <div className="right-panel">
          {/* 串珠作品上傳 */}
          <div className="bead-upload-section">
            <h3>📿 上傳你的串珠作品</h3>
            <div 
              className="upload-area" 
              onClick={triggerFileSelect}
              onDragOver={handleDragOver}
              onDrop={handleDrop}
            >
              {uploadedImage ? (
                <img src={uploadedImage} alt="串珠作品" className="uploaded-image" />
              ) : (
                <div className="upload-placeholder">
                  <div className="upload-icon">📷</div>
                  <p>點擊上傳圖片</p>
                  <p>或拖拽圖片到此處</p>
                </div>
              )}
            </div>
            <input
              type="file"
              ref={fileInputRef}
              onChange={handleImageUpload}
              accept="image/*"
              style={{ display: 'none' }}
            />
            <button 
              className="analyze-btn" 
              onClick={startAnalysis}
              disabled={!uploadedImage || isAnalyzing}
            >
              {isAnalyzing ? '🔮 分析中...' : '🔮 開始分析'}
            </button>
          </div>

          {/* 雷達圖 */}
          <div className="radar-chart-section">
            <h3>🌟 能量評分圖</h3>
            <div className="radar-chart">
              <svg viewBox="0 0 200 200" className="radar-svg">
                {/* 五邊形網格 */}
                <polygon
                  points="100,20 150,50 150,150 100,180 50,150 50,50"
                  fill="none"
                  stroke="#ddd"
                  strokeWidth="1"
                />
                <polygon
                  points="100,40 140,60 140,140 100,160 60,140 60,60"
                  fill="none"
                  stroke="#ddd"
                  strokeWidth="1"
                />
                <polygon
                  points="100,60 130,70 130,130 100,140 70,130 70,70"
                  fill="none"
                  stroke="#ddd"
                  strokeWidth="1"
                />
                <polygon
                  points="100,80 120,80 120,120 100,120 80,120 80,80"
                  fill="none"
                  stroke="#ddd"
                  strokeWidth="1"
                />

                {/* 評分填充區域 */}
                {showResult && (
                  <polygon
                    points={`100,${100 - scores.love * 8} ${100 + scores.windfall * 8},${100 - scores.windfall * 4} ${100 + scores.regularIncome * 8},${100 + scores.regularIncome * 4} ${100},${100 + scores.career * 8} ${100 - scores.health * 8},${100 - scores.health * 4}`}
                    fill="rgba(138, 43, 226, 0.3)"
                    stroke="rgba(138, 43, 226, 0.8)"
                    strokeWidth="2"
                  />
                )}

                {/* 軸線標籤 */}
                <text x="100" y="15" textAnchor="middle" className="axis-label">愛情</text>
                <text x="155" y="50" textAnchor="start" className="axis-label">偏財運</text>
                <text x="155" y="150" textAnchor="start" className="axis-label">正財運</text>
                <text x="100" y="195" textAnchor="middle" className="axis-label">事業</text>
                <text x="45" y="150" textAnchor="end" className="axis-label">健康</text>

                {/* 分數標示 */}
                {showResult && (
                  <>
                    <text x="100" y={100 - scores.love * 8 - 5} textAnchor="middle" className="score-label">{scores.love}</text>
                    <text x={100 + scores.windfall * 8 + 5} y={100 - scores.windfall * 4} textAnchor="start" className="score-label">{scores.windfall}</text>
                    <text x={100 + scores.regularIncome * 8 + 5} y={100 + scores.regularIncome * 4} textAnchor="start" className="score-label">{scores.regularIncome}</text>
                    <text x="100" y={100 + scores.career * 8 + 15} textAnchor="middle" className="score-label">{scores.career}</text>
                    <text x={100 - scores.health * 8 - 5} y={100 - scores.health * 4} textAnchor="end" className="score-label">{scores.health}</text>
                  </>
                )}
              </svg>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BeadRating;
