import React, { useState, useRef, useEffect } from 'react';
import './BeadRating.css';

const BeadRating = () => {
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [showResult, setShowResult] = useState(false);
  const [currentMessage, setCurrentMessage] = useState('');
  const [fullMessage, setFullMessage] = useState('');
  const [scores, setScores] = useState({
    love: 0,
    windfall: 0,
    regularIncome: 0,
    career: 0,
    health: 0
  });
  const [savedDesign, setSavedDesign] = useState(null);

  // ÈÄöÈùàÂ∏´ÁöÑÂÆåÊï¥Âª∫Ë≠∞
  const psychicAdvice = `‰Ω†ÁöÑ‰∏≤Áè†‰ΩúÂìÅÂ±ïÁèæ‰∫ÜÁç®ÁâπÁöÑËóùË°ìÂ§©Ë≥¶ÂíåÂÖßÂú®Êô∫ÊÖß„ÄÇÈÄô‰ª∂‰ΩúÂìÅ‰∏çÂÉÖÊòØ‰∏Ä‰ª∂ÁæéÈ∫óÁöÑÈ£æÂìÅÔºåÊõ¥ÊòØ‰Ω†ÂÖßÂøÉ‰∏ñÁïåÁöÑÁúüÂØ¶ÂØ´ÁÖß„ÄÇÂæûÊÑõÊÉÖÈÅãÂã¢‰æÜÁúãÔºåÁ¥ÖËâ≤Áè†Â≠êÁöÑÈªûÁ∂¥Ë±°ÂæµËëóÁÜ±ÊÉÖËàáÂãáÊ∞£ÔºåÈ†êÁ§∫ËëóÁæéÂ•ΩÁöÑÊÑõÊÉÖÂç≥Â∞áÂà∞‰æÜÔºåÂú®Êé•‰∏ã‰æÜÁöÑ‰∏âÂÄãÊúàÂÖßÔºå‰Ω†Â∞áÈÅáÂà∞‰∏Ä‰ΩçËàá‰Ω†ÈùàÈ≠ÇÂÖ±È≥¥ÁöÑ‰∫∫„ÄÇÂú®Ë≤°ÂØåÊñπÈù¢ÔºåËóçËâ≤Áè†Â≠êÁöÑËÉΩÈáèËàáÊ∞¥ÂÖÉÁ¥†Áõ∏ÂëºÊáâÔºåÈ†êÁ§∫ËëóÊµÅÂãïÁöÑË≤°ÂØåÂç≥Â∞áÂà∞‰æÜÔºåÂª∫Ë≠∞‰Ω†‰øùÊåÅÈñãÊîæÁöÑÂøÉÊÖãÔºåÁïôÊÑèË∫´ÈÇäÁöÑÊäïË≥áÊ©üÊúÉÔºå‰ΩÜÂàáË®ò‰∏çË¶ÅÈÅéÊñºË≤™ÂøÉÔºåÁ©©ÂÅ•ÁöÑÁêÜË≤°ÊñπÂºèÊúÉÁÇ∫‰Ω†Â∏∂‰æÜÊÑèÊÉ≥‰∏çÂà∞ÁöÑÊî∂Á©´„ÄÇ‰∫ãÊ•≠ÁôºÂ±ï‰∏äÔºå‰∏≤Áè†ÁöÑÂ∞çÁ®±ÊéíÂàóÂèçÊò†‰∫Ü‰Ω†Â∞çÂ∑•‰ΩúÁöÑË™çÁúüÊÖãÂ∫¶ÔºåËÄåÈáëËâ≤Áè†Â≠êÁöÑÈªûÁ∂¥ÂâáË±°ÂæµËëóË±êÂéöÁöÑÂõûÂ†±Ôºå‰Ω†ÁöÑÂä™ÂäõÂ∞áÂæóÂà∞Ë™çÂèØÔºåÂçáËÅ∑Âä†Ëñ™ÁöÑÊ©üÊúÉÂ∞±Âú®ÁúºÂâç„ÄÇÂæûÂâµÈÄ†ÂäõËßíÂ∫¶‰æÜÁúãÔºåÁ∂†Ëâ≤Áè†Â≠êÁöÑËÉΩÈáèËàáÊàêÈï∑Áõ∏ÂëºÊáâÔºåÈ†êÁ§∫Ëëó‰Ω†ÁöÑ‰∫ãÊ•≠Â∞áÈÄ≤ÂÖ•Âø´ÈÄüÁôºÂ±ïÊúüÔºåÊñ∞ÁöÑÈ†ÖÁõÆÊ©üÊúÉÂ∞áÊé•Ë∏µËÄåËá≥„ÄÇÂú®ÂÅ•Â∫∑ÊñπÈù¢ÔºåÊï¥È´îËÉΩÈáèÈùûÂ∏∏ÂíåË´ßÔºå‰Ω†ÁöÑ‰∏≤Áè†‰ΩúÂìÅÊï£ÁôºËëóÂπ≥Ë°°ËàáÂÅ•Â∫∑ÁöÑÊ∞£ÊÅØÔºåÁ¥´Ëâ≤Áè†Â≠êÁöÑËÉΩÈáèËàáÈùàÊÄßÁõ∏ÂëºÊáâÔºåÈ†êÁ§∫Ëëó‰Ω†ÁöÑË∫´ÂøÉÁãÄÊÖãÂ∞áÈÅîÂà∞ÊúÄ‰Ω≥ÁãÄÊÖã„ÄÇÂª∫Ë≠∞‰Ω†Â∞áÈÄô‰ªΩÂâµÈÄ†ÂäõÈÅãÁî®Âà∞ÁîüÊ¥ªÁöÑÂêÑÂÄãÊñπÈù¢ÔºåÁõ∏‰ø°Ëá™Â∑±ÁöÑÁõ¥Ë¶∫ÔºåÂãáÊï¢ËøΩÊ±ÇÂ§¢ÊÉ≥Ôºå‰øùÊåÅË¶èÂæãÁöÑ‰ΩúÊÅØÔºåÂ§öÊé•Ëß∏Â§ßËá™ÁÑ∂„ÄÇË®ò‰ΩèÔºåÊØèÂÄãÁè†Â≠êÈÉΩÊâøËºâËëóÁç®ÁâπÁöÑËÉΩÈáèÔºåÂ∞±ÂÉè‰Ω†‰∫∫Áîü‰∏≠ÁöÑÊØèÂÄãÈÅ∏ÊìáÈÉΩËòäÂê´ËëóÁÑ°ÈôêÂèØËÉΩ„ÄÇ`;

  // Ê™¢Êü•ÊòØÂê¶Êúâ‰øùÂ≠òÁöÑ‰∏≤Áè†Ë®≠Ë®à
  useEffect(() => {
    const savedDesignData = localStorage.getItem('savedBeadDesign');
    if (savedDesignData) {
      try {
        const design = JSON.parse(savedDesignData);
        setSavedDesign(design);
        console.log('ËºâÂÖ•‰øùÂ≠òÁöÑ‰∏≤Áè†Ë®≠Ë®à:', design);
      } catch (error) {
        console.error('Ëß£Êûê‰øùÂ≠òÁöÑË®≠Ë®àÊï∏ÊìöÂ§±Êïó:', error);
      }
    }
  }, []);

  // Ê†πÊìöÁè†Â≠êÂêçÁ®±ÁöÑÈõôÈù¢ÂêëË©ïÂàÜÁ≥ªÁµ±
  const getBeadDualCategoryAndScore = (beadName) => {
    // Ê†πÊìöÁè†Â≠êÂêçÁ®±‰æÜË©ïÂàÜÔºåÊØèÂÄãÁè†Â≠êÊúâÂÖ©ÂÄãÈù¢ÂêëÁöÑË©ïÂàÜ
    const beadCategories = {
      // ÁéªÁíÉÁè†Á≥ªÂàó
      'Á≤âËâ≤': { 
        primary: { category: 'love', name: 'Á≤âËâ≤ÁéªÁíÉÁè†', score: 1, description: 'ÊÑõÊÉÖËàáÂíåË´ß' },
        secondary: { category: 'health', name: 'Á≤âËâ≤ÁéªÁíÉÁè†', score: 1, description: 'ÊÉÖÊÑüÂÅ•Â∫∑' }
      },
      'ËñÑËç∑Á∂†': { 
        primary: { category: 'windfall', name: 'ËñÑËç∑Á∂†ÁéªÁíÉÁè†', score: 1, description: 'Ë≤°ÂØåËàáÊàêÂäü' },
        secondary: { category: 'career', name: 'ËñÑËç∑Á∂†ÁéªÁíÉÁè†', score: 1, description: '‰∫ãÊ•≠Ëá™‰ø°' }
      },
      'ÈªÉËâ≤': { 
        primary: { category: 'regularIncome', name: 'ÈªÉËâ≤ÁéªÁíÉÁè†', score: 1, description: 'Ê∑®ÂåñËàáÂπ≥Ë°°' },
        secondary: { category: 'health', name: 'ÈªÉËâ≤ÁéªÁíÉÁè†', score: 1, description: 'Ë∫´ÂøÉÊ∑®Âåñ' }
      },
      'Á¥ÖËâ≤': { 
        primary: { category: 'career', name: 'Á¥ÖËâ≤ÁéªÁíÉÁè†', score: 1, description: '‰øùË≠∑ËàáÊ∫ùÈÄö' },
        secondary: { category: 'windfall', name: 'Á¥ÖËâ≤ÁéªÁíÉÁè†', score: 1, description: 'Ë≤°ÈÅã‰øùË≠∑' }
      },
      'Ê∑∫Ëóç': { 
        primary: { category: 'health', name: 'Ê∑∫ËóçÁéªÁíÉÁè†', score: 1, description: 'ÈùàÊÄßËàáÂÅ•Â∫∑' },
        secondary: { category: 'love', name: 'Ê∑∫ËóçÁéªÁíÉÁè†', score: 1, description: 'Êô∫ÊÖßÊÑõÊÉÖ' }
      },
      'Ê©òËâ≤': { 
        primary: { category: 'love', name: 'Ê©òËâ≤ÁéªÁíÉÁè†', score: 1, description: 'Êµ™Êº´ËàáÁÜ±ÊÉÖ' },
        secondary: { category: 'career', name: 'Ê©òËâ≤ÁéªÁíÉÁè†', score: 1, description: '‰∫∫ÈöõÈ≠ÖÂäõ' }
      },
      
      // Á±≥Áè†Á≥ªÂàó
      'Á¥´Ëâ≤': { 
        primary: { category: 'windfall', name: 'Á¥´Ëâ≤Á±≥Áè†', score: 1, description: 'Ê¥ªÂäõËàáÂãáÊ∞£' },
        secondary: { category: 'health', name: 'Á¥´Ëâ≤Á±≥Áè†', score: 1, description: 'ÁîüÂëΩÂäõ' }
      },
      'Ê∑∫Á∂†': { 
        primary: { category: 'regularIncome', name: 'Ê∑∫Á∂†Á±≥Áè†', score: 1, description: 'Êô∫ÊÖßËàáË≤°ÂØå' },
        secondary: { category: 'love', name: 'Ê∑∫Á∂†Á±≥Áè†', score: 1, description: 'Êô∫ÊÖßÊÑõÊÉÖ' }
      },
      'Á±≥Ëâ≤': { 
        primary: { category: 'career', name: 'Á±≥Ëâ≤Á±≥Áè†', score: 1, description: '‰øùË≠∑ËàáÂäõÈáè' },
        secondary: { category: 'regularIncome', name: 'Á±≥Ëâ≤Á±≥Áè†', score: 1, description: 'Ë≤°ÈÅã‰øùË≠∑' }
      },
      'Ê∑∫Á≤â': { 
        primary: { category: 'health', name: 'Ê∑∫Á≤âÁ±≥Áè†', score: 1, description: '‰øùË≠∑ËàáÂπ≥Ë°°' },
        secondary: { category: 'windfall', name: 'Ê∑∫Á≤âÁ±≥Áè†', score: 1, description: 'Ë≤°ÈÅãÂπ≥Ë°°' }
      },
      'Â§©Ëóç': { 
        primary: { category: 'love', name: 'Â§©ËóçÁ±≥Áè†', score: 1, description: 'Ê∫´ÊüîËàáÂåÖÂÆπ' },
        secondary: { category: 'regularIncome', name: 'Â§©ËóçÁ±≥Áè†', score: 1, description: 'Ë≤°ÈÅãÂíåË´ß' }
      },
      'Ê∑°Á¥´': { 
        primary: { category: 'windfall', name: 'Ê∑°Á¥´Á±≥Áè†', score: 1, description: 'Êô∫ÊÖßËàáË≤°ÈÅã' },
        secondary: { category: 'career', name: 'Ê∑°Á¥´Á±≥Áè†', score: 1, description: '‰∫ãÊ•≠Êô∫ÊÖß' }
      },
      'ÈáëÊ£ï': { 
        primary: { category: 'regularIncome', name: 'ÈáëÊ£ïÁ±≥Áè†', score: 1, description: 'Âπ≥ÈùúËàáÁ©©ÂÆö' },
        secondary: { category: 'health', name: 'ÈáëÊ£ïÁ±≥Áè†', score: 1, description: 'ÊÉÖÁ∑íÁ©©ÂÆö' }
      },
      'Ê∑±Ëóç': { 
        primary: { category: 'career', name: 'Ê∑±ËóçÁ±≥Áè†', score: 1, description: 'Êô∫ÊÖßËàáÈùàÊÄß' },
        secondary: { category: 'love', name: 'Ê∑±ËóçÁ±≥Áè†', score: 1, description: 'ÈùàÊÄßÊÑõÊÉÖ' }
      },
      'Ê∑±Á∂†': { 
        primary: { category: 'health', name: 'Ê∑±Á∂†Á±≥Áè†', score: 1, description: 'Ê∑®ÂåñËàáÁôÇÁôí' },
        secondary: { category: 'windfall', name: 'Ê∑±Á∂†Á±≥Áè†', score: 1, description: 'Ë≤°ÈÅãÊ∑®Âåñ' }
      }
    };
    
    return beadCategories[beadName] || { 
      primary: { category: 'unknown', name: 'Êú™Áü•', score: 1, description: 'Êú™Áü•ËÉΩÈáè' },
      secondary: { category: 'unknown', score: 1, description: 'Êú™Áü•ËÉΩÈáè' }
    };
  };

  // Ê†πÊìöÁè†Â≠êË®≠Ë®àÂàÜÊûêË©ïÂàÜ
  const analyzeBeadDesign = (design) => {
    const { beads } = design;
    let scores = {
      love: 4,        // Âü∫Á§éÂàÜ1ÂàÜ + Ë®≠Ë®àÊÑüÈ°çÂ§ñÁçéÂãµ3ÂàÜ = 4ÂàÜ
      windfall: 1,    // Âü∫Á§éÂàÜ1ÂàÜ
      regularIncome: 1, // Âü∫Á§éÂàÜ1ÂàÜ
      career: 1,      // Âü∫Á§éÂàÜ1ÂàÜ
      health: 1       // Âü∫Á§éÂàÜ1ÂàÜ
    };

    console.log('ÈñãÂßãÂàÜÊûêÁè†Â≠êË®≠Ë®àÔºåÁ∏ΩÂÖ±', beads.length, 'È°ÜÁè†Â≠ê');
    console.log('ÂàùÂßãË©ïÂàÜÔºàÂü∫Á§éÂàÜ1ÂàÜ + Ë®≠Ë®àÊÑüÈ°çÂ§ñÁçéÂãµ3ÂàÜÔºâ:', scores);

    // ÂàÜÊûêÊØèÈ°ÜÁè†Â≠êÁöÑËÉΩÈáèÂ±¨ÊÄß - Ê†πÊìöÁè†Â≠êÂêçÁ®±Ë©ïÂàÜ
    beads.forEach((bead, index) => {
      const { id, name } = bead;
      
      console.log(`Áè†Â≠ê${index + 1}: ID=${id}, ÂêçÁ®±=${name}`);
      
      // Ê†πÊìöÁè†Â≠êÂêçÁ®±‰æÜË©ïÂàÜÔºå‰∏çÂÜçÈôêÂà∂IDÁØÑÂúç
      const beadInfo = getBeadDualCategoryAndScore(name);
      console.log(`Áè†Â≠ê${index + 1}:`, { 
        id, 
        name, 
        primary: beadInfo.primary, 
        secondary: beadInfo.secondary 
      });
      
      // ‰∏ªË¶ÅÈù¢ÂêëË©ïÂàÜ - Âä†1ÂàÜ
      switch (beadInfo.primary.category) {
        case 'love':
          scores.love = Math.min(10, scores.love + 1);
          console.log('ÊÑõÊÉÖÂä†‰∏ä‰∏ªË¶ÅÈù¢ÂêëÂàÜÊï∏: +1, Á∏ΩÂàÜ:', scores.love);
          break;
          
        case 'windfall':
          scores.windfall = Math.min(10, scores.windfall + 1);
          console.log('ÂÅèË≤°Âä†‰∏ä‰∏ªË¶ÅÈù¢ÂêëÂàÜÊï∏: +1, Á∏ΩÂàÜ:', scores.windfall);
          break;
          
        case 'regularIncome':
          scores.regularIncome = Math.min(10, scores.regularIncome + 1);
          console.log('Ê≠£Ë≤°Âä†‰∏ä‰∏ªË¶ÅÈù¢ÂêëÂàÜÊï∏: +1, Á∏ΩÂàÜ:', scores.regularIncome);
          break;
          
        case 'career':
          scores.career = Math.min(10, scores.career + 1);
