      console.log('ÂàÜÊûêÂÆåÊàêÔºåÊñ∞Ë©ïÂàÜ:', newScores); // Ë™øË©¶Áî®
      
      // Âº∑Âà∂Êõ¥Êñ∞Ë©ïÂàÜÁãÄÊÖã
      setScores(newScores);

      // Ê†πÊìöÁè†Â≠êË®≠Ë®àÁîüÊàêÂÄãÊÄßÂåñÂª∫Ë≠∞
      const personalizedAdvice = generatePersonalizedAdvice(savedDesign, newScores);
      setFullMessage(personalizedAdvice);

      // ÈÄêÂ≠óÈ°ØÁ§∫Âª∫Ë≠∞
      let index = 0;
      const typeWriter = () => {
        if (index < personalizedAdvice.length) {
          setCurrentMessage(personalizedAdvice.substring(0, index + 1));
          index++;
          setTimeout(typeWriter, 50);
        } else {
          setShowResult(true);
          setIsAnalyzing(false);
          console.log('ÂàÜÊûêÂÆåÊàêÔºåÈ°ØÁ§∫ÁµêÊûúÔºåË©ïÂàÜ:', newScores); // Ë™øË©¶Áî®
        }
