      ]
    };
    
    console.log('=== ÈñãÂßãÊ∏¨Ë©¶Ë©ïÂàÜÁ≥ªÁµ± ===');
    console.log('ÂâµÂª∫Ê∏¨Ë©¶Ë®≠Ë®à:', testDesign);
    console.log('Ê∏¨Ë©¶Ë®≠Ë®àÁè†Â≠êID:', testDesign.beads[0].id);
    console.log('Áè†Â≠êIDÁØÑÂúçÊ™¢Êü• (1-15):', testDesign.beads[0].id >= 1 && testDesign.beads[0].id <= 15);
    
    setSavedDesign(testDesign);
    
    // Á´ãÂç≥ÂàÜÊûêÊ∏¨Ë©¶Ë®≠Ë®à
    setTimeout(() => {
      console.log('=== ÈñãÂßãÂàÜÊûêÊ∏¨Ë©¶Ë®≠Ë®à ===');
      const testScores = analyzeBeadDesign(testDesign);
      console.log('=== ÂàÜÊûêÂÆåÊàê ===');
      console.log('Ê∏¨Ë©¶Ë®≠Ë®àË©ïÂàÜÁµêÊûú:', testScores);
      
      // Ê™¢Êü•Ë©ïÂàÜÊòØÂê¶Ê≠£Á¢∫
      console.log('È†êÊúüË©ïÂàÜ - Ë®≠Ë®àÊÑü(ÊÑõÊÉÖ):', 1 + 1 + 1); // Âü∫Á§é1 + ‰∏ªË¶Å1 + Á¨¨‰∫å1 = 3
      console.log('È†êÊúüË©ïÂàÜ - ÂÅ•Â∫∑:', 1 + 1); // Âü∫Á§é1 + Á¨¨‰∫å1 = 2
      console.log('ÂØ¶ÈöõË©ïÂàÜ:', testScores);
      
      setScores(testScores);
