              }}>
                <strong>Áï∂ÂâçË©ïÂàÜÁãÄÊÖã (Ê†πÊìöÁè†Â≠êÂêçÁ®±Ë©ïÂàÜ):</strong><br/>
                Ë®≠Ë®àÊÑü: {scores.love} | ÊÑõÊÉÖ: {scores.windfall} | ÂÅèË≤°: {scores.regularIncome}<br/>
                Ê≠£Ë≤°: {scores.career} | ‰∫ãÊ•≠: {scores.regularIncome} | ÂÅ•Â∫∑: {scores.health}<br/>
                <small>Ë®≠Ë®àÊÑüÂü∫Á§éÂàÜ4ÂàÜÔºàÂê´È°çÂ§ñÁçéÂãµ3ÂàÜÔºâÔºåÂÖ∂‰ªñÈù¢ÂêëÂü∫Á§éÂàÜ1ÂàÜÔºåÊØèÂÄãÁè†Â≠êÁÇ∫ÂÖ©ÂÄãÈù¢ÂêëÂêÑÂä†1ÂàÜÔºåÊúÄÈ´ò10ÂàÜ</small><br/>
                <small>ÊîØÊè¥ÁöÑÁè†Â≠êÔºöÁ≤âËâ≤„ÄÅËñÑËç∑Á∂†„ÄÅÈªÉËâ≤„ÄÅÁ¥ÖËâ≤„ÄÅÊ∑∫Ëóç„ÄÅÊ©òËâ≤„ÄÅÁ¥´Ëâ≤„ÄÅÊ∑∫Á∂†„ÄÅÁ±≥Ëâ≤„ÄÅÊ∑∫Á≤â„ÄÅÂ§©Ëóç„ÄÅÊ∑°Á¥´„ÄÅÈáëÊ£ï„ÄÅÊ∑±Ëóç„ÄÅÊ∑±Á∂†</small>
              </div>
            )}
            
            <div className="radar-chart">
              {/* SVGÈõ∑ÈÅîÂúñ */}
              <svg 
                className="radar-svg" 
                viewBox="0 0 350 350" 
                width="350" 
                height="350"
              >
                {/* ËÉåÊôØÁ∂≤Ê†º - ‰∏âÂÄãÂêåÂøÉÂÖ≠ÈÇäÂΩ¢ */}
                <polygon 
                  points="175,75 275,125 275,225 175,275 75,225 75,125" 
                  fill="none" 
                  stroke="rgba(255,255,255,0.3)" 
                  strokeWidth="1"
                />
                <polygon 
                  points="175,105 245,145 245,205 175,245 105,205 105,145" 
                  fill="none" 
                  stroke="rgba(255,255,255,0.3)" 
                  strokeWidth="1"
                />
                <polygon 
                  points="175,135 215,165 215,185 175,215 135,185 135,165" 
                  fill="none" 
                  stroke="rgba(255,255,255,0.3)" 
                  strokeWidth="1"
                />
                
                {/* Ëª∏Á∑ö - Âæû‰∏≠ÂøÉÂà∞ÂÖ≠ÂÄãÈ†ÇÈªû */}
                <line x1="175" y1="175" x2="175" y2="75" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                <line x1="175" y1="175" x2="275" y2="125" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                <line x1="175" y1="175" x2="275" y2="225" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                <line x1="175" y1="175" x2="175" y2="275" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                <line x1="175" y1="175" x2="75" y2="225" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                <line x1="175" y1="175" x2="75" y2="125" stroke="rgba(255,255,255,0.5)" strokeWidth="1" />
                
                {/* AIË©ïÂàÜÁµêÊûúÂ°´ÂÖÖÂçÄÂüü - ÁúüÊ≠£ÁöÑÂÖ≠ÈÇäÂΩ¢ */}
                {showResult && (() => {
                  const scorePoints = getHexagonScorePoints(scores);
                  const pointsString = scorePoints.map(point => `${point.x},${point.y}`).join(' ');
                  
                  return (
                    <polygon 
                      key={`fill-${JSON.stringify(scores)}`}
                      points={pointsString}
                      fill="rgba(138, 43, 226, 0.6)" 
                      stroke="rgba(138, 43, 226, 0.8)" 
                      strokeWidth="2"
                    />
                  );
                })()}
                
                {/* Ë©ïÂàÜÈªû */}
                {showResult && (() => {
                  const scorePoints = getHexagonScorePoints(scores);
                  
                  return (
                    <>
                      {scorePoints.map((point, index) => (
                        <circle 
                          key={`point-${index}-${JSON.stringify(scores)}`}
                          cx={point.x} 
                          cy={point.y} 
                          r="4" 
                          fill="#ffd700" 
                        />
                      ))}
                    </>
                  );
                })()}
                
                {/* Ëª∏Á∑öÊ®ôÁ±§ - Ë™øÊï¥‰ΩçÁΩÆÈÅøÂÖçË¢´Êà™Êñ∑ */}
                <text x="175" y="60" className="axis-label" textAnchor="middle">Ë®≠Ë®àÊÑü ({scores.love})</text>
                <text x="290" y="130" className="axis-label" textAnchor="start">ÊÑõÊÉÖ ({scores.windfall})</text>
                <text x="290" y="230" className="axis-label" textAnchor="start">ÂÅèË≤° ({scores.regularIncome})</text>
                <text x="175" y="300" className="axis-label" textAnchor="middle">Ê≠£Ë≤° ({scores.career})</text>
                <text x="60" y="230" className="axis-label" textAnchor="end">‰∫ãÊ•≠ ({scores.regularIncome})</text>
                <text x="60" y="130" className="axis-label" textAnchor="end">ÂÅ•Â∫∑ ({scores.health})</text>
              </svg>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BeadRating;
