import React, { useState, useEffect, useRef } from 'react';
import './StringingAnimation.css';

const StringingAnimation = ({ 
  isVisible, 
  selectedBeads, 
  stringLength, 
  stringWidth, 
  onComplete 
}) => {
  const [currentBeadIndex, setCurrentBeadIndex] = useState(0);
  const [strungBeads, setStrungBeads] = useState([]);
  const [isAnimating, setIsAnimating] = useState(false);
  const animationRef = useRef(null);

  // 當組件顯示時，開始動畫
  useEffect(() => {
    if (isVisible && selectedBeads.length > 0) {
      console.log('開始串珠動畫，珠子數量:', selectedBeads.length);
      setCurrentBeadIndex(0);
      setStrungBeads([]);
      setIsAnimating(true);
      startNextBead();
    }
  }, [isVisible, selectedBeads]);

  // 開始下一顆珠子的動畫
  const startNextBead = () => {
    if (currentBeadIndex >= selectedBeads.length) {
      console.log('所有珠子串珠完成！');
      setIsAnimating(false);
      return;
    }

    const currentBead = selectedBeads[currentBeadIndex];
    console.log(`開始串第 ${currentBeadIndex + 1} 顆珠子:`, currentBead.name);

    // 等待 DOM 渲染完成後觸發動畫
    setTimeout(() => {
      const beadElement = document.querySelector(`[data-animation-bead-id="${currentBead.id}"]`);
      if (beadElement) {
        // 計算終點位置
        const endPosition = calculateEndPosition(currentBeadIndex);
        console.log(`珠子 ${currentBeadIndex} 將滑動到:`, endPosition);

        // 觸發滑動動畫
        beadElement.style.left = endPosition.left;
        beadElement.style.top = endPosition.top;
      }
    }, 200);
  };

  // 計算珠子的終點位置
  const calculateEndPosition = (beadIndex) => {
    if (beadIndex === 0) {
      // 第一顆珠子滑到線尾
      const baseEnd = stringLength === 'half' ? 20 : 
                      stringLength === 'four-thirds' ? 25 : 30;
      return {
        left: `calc(50% + ${baseEnd}vh)`,
        top: `calc(50% + ${baseEnd}vh)`
      };
    } else {
      // 後續珠子要留出前面珠子的空間
      let totalBeadsLength = 0;
      for (let i = 0; i < beadIndex; i++) {
        const bead = selectedBeads[i];
        const size = bead.type === '過渡珠' ? 15 : 
                    (bead.type === '米珠' || bead.type === '珍珠') ? 21 : 
                    (bead.type === '米珠') ? 40 : 60;
        totalBeadsLength += size;
      }

      const baseEnd = stringLength === 'half' ? 20 : 
                      stringLength === 'four-thirds' ? 25 : 30;
      const endOffset = (totalBeadsLength / 100) * baseEnd;

      return {
        left: `calc(50% + ${baseEnd - endOffset}vh)`,
        top: `calc(50% + ${baseEnd - endOffset}vh)`
      };
    }
  };

  // 處理珠子動畫完成
  const handleBeadAnimationComplete = (beadId) => {
    const currentBead = selectedBeads[currentBeadIndex];
    const endPosition = calculateEndPosition(currentBeadIndex);

    console.log(`珠子 ${currentBeadIndex} 動畫完成，添加到已串好列表`);

    // 將當前珠子添加到已串好列表
    setStrungBeads(prev => [...prev, {
      ...currentBead,
      finalPosition: endPosition
    }]);

    // 準備下一顆珠子
    const nextIndex = currentBeadIndex + 1;
    if (nextIndex < selectedBeads.length) {
      setCurrentBeadIndex(nextIndex);
      // 延遲一下再開始下一顆
      setTimeout(() => {
        startNextBead();
      }, 1000);
    } else {
      // 所有珠子都串完了
      console.log('所有珠子串珠完成！');
      setIsAnimating(false);
    }
  };

  // 完成串珠
  const handleComplete = () => {
    onComplete();
  };

  if (!isVisible) return null;

  return (
    <div className="stringing-animation-overlay">
      {/* 串珠線 */}
      <div className="stringing-line">
        <div className="line-segment line-start"></div>
        <div className="line-segment line-middle"></div>
        <div className="line-segment line-end"></div>
      </div>

      {/* 已串好的珠子 */}
      {strungBeads.map((bead, index) => (
        <div
          key={`strung-${bead.id}-${index}`}
          className="strung-bead"
          style={{
            position: 'absolute',
            left: bead.finalPosition.left,
            top: bead.finalPosition.top,
            zIndex: 999,
            transform: 'translate(-50%, -50%)',
            transition: 'none',
            animation: 'none',
            pointerEvents: 'none'
          }}
        >
          <img
            src={bead.image}
            alt={bead.name}
            style={{
              width: bead.type === '過渡珠' ? '15px' :
                     (bead.type === '米珠' || bead.type === '珍珠') ? '21px' :
                     (bead.type === '米珠') ? '40px' : '60px',
              height: bead.type === '過渡珠' ? '15px' :
                      (bead.type === '米珠' || bead.type === '珍珠') ? '21px' :
                      (bead.type === '米珠') ? '40px' : '60px',
              objectFit: 'contain',
              borderRadius: '50%'
            }}
          />
        </div>
      ))}

      {/* 當前正在串的珠子 */}
      {isAnimating && currentBeadIndex < selectedBeads.length && (
        <div
          className="current-bead"
          data-animation-bead-id={selectedBeads[currentBeadIndex].id}
          style={{
            position: 'absolute',
            left: stringLength === 'half' ? 'calc(50% - 20vh)' :
                  stringLength === 'four-thirds' ? 'calc(50% - 25vh)' :
                  'calc(50% - 30vh)',
            top: stringLength === 'half' ? 'calc(50% - 20vh)' :
                 stringLength === 'four-thirds' ? 'calc(50% - 25vh)' :
                 'calc(50% - 30vh)',
            zIndex: 1000,
            transition: 'all 3s ease-in-out'
          }}
          onTransitionEnd={() => handleBeadAnimationComplete(selectedBeads[currentBeadIndex].id)}
        >
          <img
            src={selectedBeads[currentBeadIndex].image}
            alt={selectedBeads[currentBeadIndex].name}
            style={{
              width: selectedBeads[currentBeadIndex].type === '過渡珠' ? '15px' :
                     (selectedBeads[currentBeadIndex].type === '米珠' || selectedBeads[currentBeadIndex].type === '珍珠') ? '21px' :
                     (selectedBeads[currentBeadIndex].type === '米珠') ? '40px' : '60px',
              height: selectedBeads[currentBeadIndex].type === '過渡珠' ? '15px' :
                      (selectedBeads[currentBeadIndex].type === '米珠' || selectedBeads[currentBeadIndex].type === '珍珠') ? '21px' :
                      (selectedBeads[currentBeadIndex].type === '米珠') ? '40px' : '60px',
              objectFit: 'contain',
              borderRadius: '50%'
            }}
          />
        </div>
      )}

      {/* 完成按鈕 */}
      <button
        className="complete-stringing-btn"
        onClick={handleComplete}
        style={{
          position: 'absolute',
          bottom: '20px',
          right: '20px',
          padding: '10px 20px',
          fontSize: '16px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          zIndex: 1001
        }}
      >
        完成串珠
      </button>
    </div>
  );
};

export default StringingAnimation;
