import React, { useState, useRef, useEffect } from 'react';
import './BeadRating.css';

const BeadRating = () => {
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [showResult, setShowResult] = useState(false);
  const [currentMessage, setCurrentMessage] = useState('');
  const [fullMessage, setFullMessage] = useState('');
  const [scores, setScores] = useState({
    love: 0,
    windfall: 0,
    regularIncome: 0,
    career: 0,
    health: 0
  });
  const [savedDesign, setSavedDesign] = useState(null);
  const [customRadarImage, setCustomRadarImage] = useState(null);

  // 通靈師的完整建議
  const psychicAdvice = `你的串珠作品展現了獨特的藝術天賦和內在智慧。這件作品不僅是一件美麗的飾品，更是你內心世界的真實寫照。從愛情運勢來看，紅色珠子的點綴象徵著熱情與勇氣，預示著美好的愛情即將到來，在接下來的三個月內，你將遇到一位與你靈魂共鳴的人。在財富方面，藍色珠子的能量與水元素相呼應，預示著流動的財富即將到來，建議你保持開放的心態，留意身邊的投資機會，但切記不要過於貪心，穩健的理財方式會為你帶來意想不到的收穫。事業發展上，串珠的對稱排列反映了你對工作的認真態度，而金色珠子的點綴則象徵著豐厚的回報，你的努力將得到認可，升職加薪的機會就在眼前。從創造力角度來看，綠色珠子的能量與成長相呼應，預示著你的事業將進入快速發展期，新的項目機會將接踵而至。在健康方面，整體能量非常和諧，你的串珠作品散發著平衡與健康的氣息，紫色珠子的能量與靈性相呼應，預示著你的身心狀態將達到最佳狀態。建議你將這份創造力運用到生活的各個方面，相信自己的直覺，勇敢追求夢想，保持規律的作息，多接觸大自然。記住，每個珠子都承載著獨特的能量，就像你人生中的每個選擇都蘊含著無限可能。`;

  // 檢查是否有保存的串珠設計
  useEffect(() => {
    const savedDesignData = localStorage.getItem('savedBeadDesign');
    if (savedDesignData) {
      try {
        const design = JSON.parse(savedDesignData);
        setSavedDesign(design);
        console.log('載入保存的串珠設計:', design);
      } catch (error) {
        console.error('解析保存的設計數據失敗:', error);
      }
    }
  }, []);

  // 模擬AI分析過程
  const startAnalysis = () => {
    if (!savedDesign) {
      alert('請先在珠子收納櫃保存串珠設計！');
      return;
    }

    setIsAnalyzing(true);
    setCurrentMessage('');
    setShowResult(false);

    // 模擬分析過程
    setTimeout(() => {
      // 生成隨機評分
      const newScores = {
        love: Math.floor(Math.random() * 4) + 7, // 7-10分
        windfall: Math.floor(Math.random() * 4) + 6, // 6-9分
        regularIncome: Math.floor(Math.random() * 4) + 7, // 7-10分
        career: Math.floor(Math.random() * 4) + 6, // 6-9分
        health: Math.floor(Math.random() * 4) + 7 // 7-10分
      };
      setScores(newScores);

      // 逐字顯示建議
      let index = 0;
      const typeWriter = () => {
        if (index < psychicAdvice.length) {
          setCurrentMessage(psychicAdvice.substring(0, index + 1));
          index++;
          setTimeout(typeWriter, 50);
        } else {
          setShowResult(true);
          setIsAnalyzing(false);
        }
      };
      typeWriter();
    }, 2000);
  };

  // 清除保存的設計
  const clearSavedDesign = () => {
    localStorage.removeItem('savedBeadDesign');
    setSavedDesign(null);
    setShowResult(false);
    setCurrentMessage('');
    setFullMessage('');
  };

  // 前往珠子收納櫃創建新設計
  const goToBeadCabinet = () => {
    window.location.href = '/';
  };

  // 返回主頁
  const goHome = () => {
    window.open('http://127.0.0.1:5500/index.html', '_self');
  };

  // 處理圖片上傳
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setCustomRadarImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // 清除自定義圖片
  const clearCustomImage = () => {
    setCustomRadarImage(null);
  };

  return (
    <div className="bead-rating-container">
      {/* 返回按鈕 */}
      <div className="rating-header">
        <button className="back-btn" onClick={goHome}>
          🏠 返回主頁
        </button>
        <h1>⭐ 串珠評分區 ⭐</h1>
      </div>

      <div className="rating-content">
        {/* 左側：通靈師和對話框 */}
        <div className="left-panel">
          {/* 左上：對話框 */}
          <div className="dialogue-box">
            {!isAnalyzing && !showResult && (
              <div className="welcome-message">
                <p>歡迎來到神秘占卜殿堂，讓我來解讀你的串珠設計...</p>
                {savedDesign ? (
                  <p>我看到你精心設計的串珠作品，讓我為你揭示其中的奧秘。</p>
                ) : (
                  <p>請先在珠子收納櫃創建並保存您的串珠設計，我將為你揭示其中的奧秘。</p>
                )}
              </div>
            )}

            {isAnalyzing && (
              <div className="analyzing-message">
                <div className="crystal-animation">🔮</div>
                <p>我正在解讀你的串珠作品...</p>
                <p>水晶球正在揭示命運的奧秘...</p>
              </div>
            )}

            {showResult && (
              <div className="result-message">
                <p className="advice-text">{currentMessage}</p>
                {currentMessage.length < psychicAdvice.length && (
                  <div className="typing-indicator">|</div>
                )}
              </div>
            )}
          </div>

          {/* 左中下：AI虛擬通靈師 */}
          <div className="psychic-medium">
            <div className="psychic-avatar">
              <div className="psychic-character">
                <img 
                  src="/psychic-medium.png" 
                  alt="神秘通靈師" 
                  className="psychic-image"
                />
              </div>
              <div className="psychic-title">神秘通靈師</div>
              <div className="psychic-subtitle">✨ 能量分析專家 ✨</div>
            </div>
          </div>
        </div>

        {/* 右側：作品上傳和雷達圖 */}
        <div className="right-panel">
          {/* 串珠設計展示 */}
          <div className="bead-design-section">
            <h3>📿 你的串珠設計</h3>
            {savedDesign ? (
              <div className="design-display">
                <div className="design-info">
                  <h4>{savedDesign.designName}</h4>
                  <p>包含 {savedDesign.beads.length} 顆珠子</p>
                  <p>創建時間: {new Date(savedDesign.timestamp).toLocaleString()}</p>
                </div>
                
                {/* 串珠設計視覺化 */}
                <div className="bead-design-visualization">
                  <div className="bead-string-preview">
                    {savedDesign.beads.map((bead, index) => (
                      <div 
                        key={index}
                        className="design-bead"
                        style={{ 
                          backgroundColor: bead.color,
                          animationDelay: `${index * 0.1}s`
                        }}
                        title={`${bead.name} - ${bead.type}`}
                      >
                        <div className="bead-shine"></div>
                        <div className="bead-reflection"></div>
                        <div className="bead-glow" style={{ boxShadow: `0 0 15px ${bead.shine}` }}></div>
                      </div>
                    ))}
                  </div>
                </div>
                
                <div className="design-controls">
                  <button 
                    className="analyze-btn" 
                    onClick={startAnalysis}
                    disabled={isAnalyzing}
                  >
                    {isAnalyzing ? '🔮 分析中...' : '🔮 開始分析'}
                  </button>
                  <button 
                    className="clear-design-btn" 
                    onClick={clearSavedDesign}
                  >
                    🗑️ 清除設計
                  </button>
                </div>
              </div>
            ) : (
              <div className="no-design-message">
                <div className="no-design-icon">🎨</div>
                <h4>還沒有串珠設計</h4>
                <p>請先到珠子收納櫃創建並保存您的串珠設計</p>
                <button 
                  className="go-to-cabinet-btn" 
                  onClick={goToBeadCabinet}
                >
                  🏠 前往珠子收納櫃
                </button>
              </div>
            )}
          </div>

          {/* 雷達圖 */}
          <div className="radar-chart-section">
            <h3>🌟 能量評分圖</h3>
            
            {/* 圖片上傳控制 */}
            <div className="image-upload-controls">
              <label htmlFor="radar-image-upload" className="upload-btn">
                📁 上傳雷達圖
              </label>
              <input
                id="radar-image-upload"
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                style={{ display: 'none' }}
              />
              {customRadarImage && (
                <button onClick={clearCustomImage} className="clear-image-btn">
                  🗑️ 清除圖片
                </button>
              )}
            </div>
            
            <div className="radar-chart">
              {customRadarImage ? (
                // 顯示上傳的自定義圖片
                <img 
                  src={customRadarImage} 
                  alt="自定義雷達圖" 
                  className="custom-radar-image"
                  style={{ maxWidth: '100%', height: 'auto' }}
                />
              ) : (
                // 顯示預設的SVG雷達圖
                <svg viewBox="0 0 220 220" className="radar-svg">
                  {/* 六邊形雷達圖主體 - 完全按照圖片設計 */}
                  {/* 六邊形外輪廓 - 白色粗線條 */}
                  <polygon
                    points="100,30 170,50 170,150 100,170 30,150 30,50"
                    fill="none"
                    stroke="white"
                    strokeWidth="3"
                  />
                  
                  {/* 六邊形填充 - 淺灰色 */}
                  <polygon
                    points="100,30 170,50 170,150 100,170 30,150 30,50"
                    fill="#d3d3d3"
                    fillOpacity="0.3"
                  />
                  
                  {/* 六條輻射線 - 從中心到頂點 */}
                  <line x1="100" y1="100" x2="100" y2="30" stroke="white" strokeWidth="1" />
                  <line x1="100" y1="100" x2="170" y2="50" stroke="white" strokeWidth="1" />
                  <line x1="100" y1="100" x2="170" y2="150" stroke="white" strokeWidth="1" />
                  <line x1="100" y1="100" x2="100" y2="170" stroke="white" strokeWidth="1" />
                  <line x1="100" y1="100" x2="30" y2="150" stroke="white" strokeWidth="1" />
                  <line x1="100" y1="100" x2="30" y2="50" stroke="white" strokeWidth="1" />
                  
                  {/* 內部圓形線條 */}
                  <circle cx="100" cy="100" r="50" fill="none" stroke="white" strokeWidth="1" />
                  
                  {/* 六個頂點圓點 */}
                  <circle cx="100" cy="30" r="3" fill="white" />
                  <circle cx="170" cy="50" r="3" fill="white" />
                  <circle cx="170" cy="150" r="3" fill="white" />
                  <circle cx="100" cy="170" r="3" fill="white" />
                  <circle cx="30" cy="150" r="3" fill="white" />
                  <circle cx="30" cy="50" r="3" fill="white" />
                  
                  {/* 中心圓點 */}
                  <circle cx="100" cy="100" r="4" fill="white" />
                  
                  {/* 六個字母 'e' 標籤 */}
                  <text x="100" y="65" textAnchor="middle" fill="#666" fontSize="12" transform="rotate(0, 100, 65)">e</text>
                  <text x="135" y="75" textAnchor="middle" fill="#666" fontSize="12" transform="rotate(60, 135, 75)">e</text>
                  <text x="135" y="125" textAnchor="middle" fill="#666" fontSize="12" transform="rotate(120, 135, 125)">e</text>
                  <text x="100" y="135" textAnchor="middle" fill="#666" fontSize="12" transform="rotate(180, 100, 135)">e</text>
                  <text x="65" y="125" textAnchor="middle" fill="#666" fontSize="12" transform="rotate(240, 65, 125)">e</text>
                  <text x="65" y="75" textAnchor="middle" fill="#666" fontSize="12" transform="rotate(300, 65, 75)">e</text>

                  {/* 六邊形軸線標籤 - 調整到外面避免被擋到 */}
                  <text x="110" y="15" textAnchor="middle" fill="white" fontSize="14">愛情</text>
                  <text x="195" y="45" textAnchor="start" fill="white" fontSize="14">偏財</text>
                  <text x="195" y="155" textAnchor="start" fill="white" fontSize="14">正財</text>
                  <text x="110" y="195" textAnchor="middle" fill="white" fontSize="14">事業</text>
                  <text x="25" y="155" textAnchor="end" fill="white" fontSize="14">健康</text>
                  <text x="25" y="45" textAnchor="end" fill="white" fontSize="14">設計感</text>
                </svg>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BeadRating;
