import React from 'react';
import BeadCabinet from './BeadCabinet';
import DebugFloatingBeads from './DebugFloatingBeads';
import HomePage from './HomePage';
import BeadTutorial from './BeadTutorial';
import BeadPatterns from './BeadPatterns';
import './App.css';

function App() {
  // 檢查URL路徑來決定顯示哪個組件
  const path = window.location.pathname;
  
  if (path === '/debug') {
    return <DebugFloatingBeads />;
  }
  
  if (path === '/cabinet') {
    return (
      <div className="App">
        <BeadCabinet />
      </div>
    );
  }
  
  if (path === '/tutorial') {
    return <BeadTutorial />;
  }
  
  if (path === '/patterns') {
    return <BeadPatterns />;
  }
  
  // 預設顯示首頁
  return <HomePage />;
}

export default App;
