                  const largeArcFlag = Math.abs(endAngle - startAngle) > Math.PI ? 1 : 0;
                  return `M ${startX} ${startY} A ${r} ${r} 0 ${largeArcFlag} 1 ${endX} ${endY}`;
                }
              })()}
