import React, { useState, useRef, useEffect } from 'react';
import './BeadCabinet.css';

// 浮空珠子組件
const FloatingBead = ({ drawer, drawerId, onClose, onDragStart, onDragEnd }) => {
  const [position, setPosition] = useState({ top: 0, left: 0 });
  const [isDragging, setIsDragging] = useState(false);
  
  useEffect(() => {
    const updatePosition = () => {
      const drawerElement = document.querySelector(`[data-drawer-id="${drawerId}"]`);
      if (drawerElement) {
        const rect = drawerElement.getBoundingClientRect();
        
        setPosition({
          top: rect.top - 150, // 在抽屜上方150px，給說明視窗留更多空間
          left: rect.left + rect.width / 2
        });
      }
    };
    
    // 立即更新位置
    updatePosition();
    
    // 監聽視窗大小變化
    window.addEventListener('resize', updatePosition);
    
    return () => {
      window.removeEventListener('resize', updatePosition);
    };
  }, [drawerId]);
  
  return (
    <div 
      className="floating-bead-container"
      style={{
        top: `${position.top}px`,
        left: `${position.left}px`,
        transform: 'translateX(-50%)',
        zIndex: 2000,
        pointerEvents: 'auto'
      }}
    >
      <div className="floating-bead-info">
        <div className="floating-bead-text">
          <h4>{drawer.name}</h4>
          <small>{drawer.type}</small>
        </div>
        <button 
          className="close-floating-btn"
          onClick={onClose}
        >
          ✕
        </button>
      </div>
      <div
        className={`floating-bead ${isDragging ? 'dragging' : ''}`}
        draggable
        onDragStart={(e) => {
          setIsDragging(true);
          onDragStart(drawer, e);
        }}
        onDragEnd={(e) => {
          setIsDragging(false);
          onDragEnd(e);
        }}
        style={{
          backgroundColor: drawer.color,
          position: 'absolute',
          top: '20%',
          left: '50%',
          transform: 'translateX(-50%)',
          zIndex: 1001
        }}
        title={`拖曳 ${drawer.name}`}
      >
        <div className="bead-shine"></div>
        <div className="bead-reflection"></div>
        <div className="bead-glow" style={{ boxShadow: `0 0 20px ${drawer.shine}` }}></div>
      </div>
    </div>
  );
};

// 木質串珠盤組件
const WoodenBeadTray = () => {
  const [selectedBeads, setSelectedBeads] = useState([]);
  const [isDraggingOver, setIsDraggingOver] = useState(false);

  // 處理拖曳放置
  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDraggingOver(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setIsDraggingOver(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDraggingOver(false);
    
    try {
      const beadData = e.dataTransfer.getData('application/json');
      if (beadData) {
        const bead = JSON.parse(beadData);
        setSelectedBeads(prev => [...prev, { ...bead, id: Date.now() }]);
      }
    } catch (error) {
      console.log('拖曳數據解析失敗:', error);
    }
  };

  // 移除珠子
  const removeBead = (beadId) => {
    setSelectedBeads(prev => prev.filter(bead => bead.id !== beadId));
  };

  return (
    <div className="wooden-bead-tray-container">
      <div className="tray-title">
        <h3>🌳 木質串珠盤</h3>
      </div>
      
      <div 
        className={`wooden-bead-tray ${isDraggingOver ? 'drag-over' : ''}`}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        {/* 同心圓設計區域 */}
        <div className="concentric-circles">
          <div className="circle outer-circle"></div>
        </div>
        
                       {/* 串珠區域 */}
               <div className="bead-stringing-area">
                 {selectedBeads.length === 0 ? (
                   <div className="empty-tray-message">
                     <div className="drop-zone-indicator">
                     </div>
                   </div>
                 ) : (
            <div className="bead-string">
              {selectedBeads.map((bead, index) => (
                <div 
                  key={bead.id}
                  className="stringed-bead"
                  style={{ 
                    backgroundColor: bead.color,
                    animationDelay: `${index * 0.1}s`
                  }}
                  onClick={() => removeBead(bead.id)}
                  title={`點擊移除 ${bead.name}`}
                >
                  <div className="bead-shine"></div>
                  <div className="bead-reflection"></div>
                  <div className="bead-glow" style={{ boxShadow: `0 0 15px ${bead.shine}` }}></div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
      
      {/* 串珠統計 */}
      <div className="stringing-stats">
        <div className="stat-item">
          <span className="stat-label">已串珠子:</span>
          <span className="stat-value">{selectedBeads.length}</span>
        </div>
        <div className="stat-item">
          <span className="stat-label">串珠長度:</span>
          <span className="stat-value">{selectedBeads.length * 8}mm</span>
        </div>
      </div>
      
      {/* 操作按鈕 */}
      <div className="tray-controls">
        <button 
          className="tray-btn clear-btn"
          onClick={() => setSelectedBeads([])}
          disabled={selectedBeads.length === 0}
        >
          🗑️ 清空串珠
        </button>
        <button 
          className="tray-btn save-btn"
          onClick={() => {
            if (selectedBeads.length > 0) {
              alert(`已保存串珠設計！包含 ${selectedBeads.length} 顆珠子`);
            }
          }}
          disabled={selectedBeads.length === 0}
        >
          💾 保存設計
        </button>
      </div>
    </div>
  );
};

const BeadCabinet = () => {
  const [openDrawers, setOpenDrawers] = useState({});
  const [selectedDrawer, setSelectedDrawer] = useState(null);
  const [draggedBead, setDraggedBead] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [extractingBead, setExtractingBead] = useState(null);
  const [extractedBeads, setExtractedBeads] = useState([]);
  const [showExtractionAnimation, setShowExtractionAnimation] = useState(false);
  const [floatingBeads, setFloatingBeads] = useState({});

  // 調試浮空狀態變化
  useEffect(() => {
    console.log('浮空狀態變化:', floatingBeads);
    console.log('浮空狀態鍵值:', Object.keys(floatingBeads));
  }, [floatingBeads]);

  // 重置浮空狀態（清除錯誤的數字鍵值）
  useEffect(() => {
    // 檢查是否有數字鍵值，如果有則清除
    const hasNumericKeys = Object.keys(floatingBeads).some(key => !isNaN(key));
    if (hasNumericKeys) {
      console.log('檢測到數字鍵值，清除浮空狀態');
      setFloatingBeads({});
    }
  }, []);

  const toggleDrawer = (drawerId) => {
    console.log('點擊抽屜:', drawerId);
    console.log('抽屜ID類型:', typeof drawerId);
    console.log('當前抽屜狀態:', openDrawers);
    console.log('當前浮空狀態:', floatingBeads);
    
    // 如果抽屜已經打開，則觸發浮空展示
    if (openDrawers[drawerId]) {
      console.log('抽屜已打開，觸發浮空展示');
      setFloatingBeads(prev => {
        const newFloatingState = {
          ...prev,
          [drawerId]: !prev[drawerId]
        };
        console.log('新的浮空狀態:', newFloatingState);
        return newFloatingState;
      });
      return;
    }
    
    // 否則打開抽屜
    console.log('打開抽屜');
    setOpenDrawers(prev => {
      const newDrawerState = {
        ...prev,
        [drawerId]: true
      };
      console.log('新的抽屜狀態:', newDrawerState);
      return newDrawerState;
    });
    setSelectedDrawer(drawerId);
  };

  // 關閉抽屜
  const closeDrawer = (drawerId, event) => {
    event.stopPropagation();
    console.log('關閉抽屜:', drawerId);
    setOpenDrawers(prev => ({
      ...prev,
      [drawerId]: false
    }));
    // 同時關閉浮空展示
    setFloatingBeads(prev => ({
      ...prev,
      [drawerId]: false
    }));
  };

  // 定義珠子的顏色和類型，以及對應的內部頁面
  const beadColors = [
    { id: 1, color: '#FF6B9D', name: '粉色', type: '玻璃珠', page: '/tutorial', shine: '#FFB6C1' },
    { id: 2, color: '#4ECDC4', name: '薄荷綠', type: '玻璃珠', page: '/patterns', shine: '#98FB98' },
    { id: 3, color: '#FFE66D', name: '黃色', type: '玻璃珠', page: '/tutorial', shine: '#FFFFE0' },
    { id: 4, color: '#FF6B6B', name: '紅色', type: '玻璃珠', page: '/patterns', shine: '#FFB6C1' },
    { id: 5, color: '#4ECDC4', name: '淺藍', type: '玻璃珠', page: '/tutorial', shine: '#87CEEB' },
    { id: 6, color: '#FFA07A', name: '橘色', type: '玻璃珠', page: '/patterns', shine: '#FFD700' },
    { id: 7, color: '#DDA0DD', name: '紫色', type: '米珠', page: '/tutorial', shine: '#E6E6FA' },
    { id: 8, color: '#98FB98', name: '淺綠', type: '米珠', page: '/patterns', shine: '#F0FFF0' },
    { id: 9, color: '#F0E68C', name: '米色', type: '米珠', page: '/tutorial', shine: '#FFF8DC' },
    { id: 10, color: '#FFB6C1', name: '淺粉', type: '米珠', page: '/patterns', shine: '#FFF0F5' },
    { id: 11, color: '#87CEEB', name: '天藍', type: '米珠', page: '/tutorial', shine: '#F0F8FF' },
    { id: 12, color: '#DDA0DD', name: '淡紫', type: '米珠', page: '/patterns', shine: '#F8F8FF' },
    { id: 13, color: '#F0E68C', name: '金棕', type: '米珠', page: '/tutorial', shine: '#FFFACD' },
    { id: 14, color: '#4682B4', name: '深藍', type: '米珠', page: '/patterns', shine: '#F0F8FF' },
    { id: 15, color: '#228B22', name: '深綠', type: '米珠', page: '/tutorial', shine: '#F0FFF0' },
    { id: 16, color: '#DC143C', name: '深紅', type: '米珠', page: '/patterns', shine: '#FFF0F5' },
    { id: 17, color: '#40E0D0', name: '青色', type: '米珠', page: '/tutorial', shine: '#F0FFFF' },
    { id: 18, color: '#98FB98', name: '薄荷綠', type: '米珠', page: '/patterns', shine: '#F0FFF0' },
    { id: 19, color: '#FFD700', name: '金色', type: '玻璃珠', page: '/tutorial', shine: '#FFFACD' },
    { id: 20, color: '#FF69B4', name: '深粉', type: '玻璃珠', page: '/patterns', shine: '#FFF0F5' },
    { id: 21, color: '#00CED1', name: '深青', type: '玻璃珠', page: '/tutorial', shine: '#F0FFFF' },
    { id: 22, color: '#FF6347', name: '番茄紅', type: '玻璃珠', page: '/patterns', shine: '#FFF5EE' },
    { id: 23, color: '#32CD32', name: '萊姆綠', type: '玻璃珠', page: '/tutorial', shine: '#F0FFF0' },
    { id: 24, color: '#9370DB', name: '中紫', type: '玻璃珠', page: '/patterns', shine: '#F8F8FF' },
    { id: 25, color: '#FF4500', name: '橙紅', type: '玻璃珠', page: '/tutorial', shine: '#FFF5EE' }
  ];

  // 創建抽屜陣列 - 左側5層3格，右側5層2格
  const leftDrawers = [];
  const rightDrawers = [];

  // 左側抽屜 (5層 x 3格 = 15個)
  for (let row = 0; row < 5; row++) {
    for (let col = 0; col < 3; col++) {
      const index = row * 3 + col;
      if (index < beadColors.length) {
        const drawerId = `left-${row}-${col}`;
        console.log(`創建左側抽屜: ${drawerId}, 索引: ${index}, 珠子: ${beadColors[index].name}`);
        const { id, ...beadData } = beadColors[index]; // 移除原始的id屬性
        leftDrawers.push({
          id: drawerId,
          ...beadData
        });
      }
    }
  }

  // 右側抽屜 (5層 x 2格 = 10個)
  for (let row = 0; row < 5; row++) {
    for (let col = 0; col < 2; col++) {
      const index = 15 + row * 2 + col;
      if (index < beadColors.length) {
        const drawerId = `right-${row}-${col}`;
        console.log(`創建右側抽屜: ${drawerId}, 索引: ${index}, 珠子: ${beadColors[index].name}`);
        const { id, ...beadData } = beadColors[index]; // 移除原始的id屬性
        rightDrawers.push({
          id: drawerId,
          ...beadData
        });
      }
    }
  }

  // 處理內部頁面導航
  const handleInternalNavigation = (page, event) => {
    event.stopPropagation();
    window.location.href = page;
  };

  // 處理詳細資訊
  const handleDetailInfo = (drawerId, event) => {
    event.stopPropagation();
    console.log(`查看抽屜 ${drawerId} 的詳細資訊`);
  };

  // 開始拖曳珠子
  const handleDragStart = (bead, event) => {
    console.log('開始拖曳珠子:', bead);
    console.log('拖曳事件:', event);
    setDraggedBead(bead);
    setIsDragging(true);
    event.dataTransfer.effectAllowed = 'copy';
    event.dataTransfer.setData('text/plain', JSON.stringify(bead));
    event.dataTransfer.setData('application/json', JSON.stringify(bead));
    console.log('拖曳數據已設置');
    console.log('設置的數據:', event.dataTransfer.getData('application/json'));
  };

  // 拖曳結束
  const handleDragEnd = () => {
    console.log('拖曳結束');
    setIsDragging(false);
    setDraggedBead(null);
  };

  // 取出珠子動畫
  const extractBead = (bead, event) => {
    event.stopPropagation();
    setExtractingBead(bead);
    setShowExtractionAnimation(true);
    
    // 創建取出動畫
    setTimeout(() => {
      setExtractedBeads(prev => [...prev, { ...bead, id: Date.now(), extractedAt: Date.now() }]);
      setShowExtractionAnimation(false);
      setExtractingBead(null);
    }, 1500);
  };

  // 清理過期的取出珠子
  useEffect(() => {
    const interval = setInterval(() => {
      setExtractedBeads(prev => 
        prev.filter(bead => Date.now() - bead.extractedAt < 5000)
      );
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="bead-cabinet-container">
      <div className="cabinet-title">
        <h1>✨ 珠子收納櫃 ✨</h1>
        <p>點擊抽屜查看珠子</p>
      </div>
      
      <div className="main-content">
        {/* 左側收納櫃區域 */}
        <div className="cabinet-section">
          <div className="bead-cabinet">
            {/* 左側收納櫃 */}
            <div className="cabinet-unit left-unit">
              <div className="cabinet-frame">
                {leftDrawers.map((drawer) => (
                  <div key={drawer.id} className="drawer-container">
                    <div 
                      className={`drawer ${openDrawers[drawer.id] ? 'open' : ''}`}
                      onClick={() => toggleDrawer(drawer.id)}
                      style={{ cursor: 'pointer' }}
                      data-drawer-id={drawer.id}
                    >
                      <div className="drawer-front">
                        <div className="drawer-handle"></div>
                        <div className="bead-preview">
                          <div 
                            className="bead-sample" 
                            style={{ backgroundColor: drawer.color }}
                          ></div>
                        </div>
                      </div>
                      <div className="drawer-content">
                        <div className="bead-info">
                          <div className="drawer-header">
                            <h3>{drawer.name}</h3>
                            <button 
                              className="close-drawer-btn"
                              onClick={(e) => closeDrawer(drawer.id, e)}
                              title="關閉抽屜"
                            >
                              ✕
                            </button>
                          </div>
                          <p>{drawer.type}</p>
                          <div className="bead-pile">
                            {[...Array(12)].map((_, i) => (
                              <div 
                                key={i}
                                className="bead"
                                style={{ 
                                  backgroundColor: drawer.color,
                                  left: `${Math.random() * 60}%`,
                                  top: `${Math.random() * 60}%`,
                                  animationDelay: `${i * 0.1}s`
                                }}
                              ></div>
                            ))}
                          </div>
                          <div className="drawer-links">
                            <button 
                              className="link-btn external-link"
                              onClick={(e) => handleInternalNavigation(drawer.page, e)}
                            >
                              📖 相關頁面
                            </button>
                            <div 
                              className="draggable-bead"
                              draggable
                              onDragStart={(e) => handleDragStart(drawer, e)}
                              onDragEnd={handleDragEnd}
                              style={{ backgroundColor: drawer.color }}
                              title={`拖曳 ${drawer.name}`}
                            >
                              ➕ 拖曳珠子
                            </div>
                            <button 
                              className="extract-bead-btn"
                              onClick={(e) => extractBead(drawer, e)}
                              style={{ 
                                backgroundColor: drawer.color,
                                boxShadow: `0 0 10px ${drawer.shine}`
                              }}
                            >
                              ✨ 取出珠子
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* 右側收納櫃 */}
            <div className="cabinet-unit right-unit">
              <div className="cabinet-frame">
                {rightDrawers.map((drawer) => (
                  <div key={drawer.id} className="drawer-container">
                    <div 
                      className={`drawer ${openDrawers[drawer.id] ? 'open' : ''}`}
                      onClick={() => toggleDrawer(drawer.id)}
                      style={{ cursor: 'pointer' }}
                      data-drawer-id={drawer.id}
                    >
                      <div className="drawer-front">
                        <div className="drawer-handle"></div>
                        <div className="bead-preview">
                          <div 
                            className="bead-sample" 
                            style={{ backgroundColor: drawer.color }}
                          ></div>
                        </div>
                      </div>
                      <div className="drawer-content">
                        <div className="bead-info">
                          <div className="drawer-header">
                            <h3>{drawer.name}</h3>
                            <button 
                              className="close-drawer-btn"
                              onClick={(e) => closeDrawer(drawer.id, e)}
                              title="關閉抽屜"
                            >
                              ✕
                            </button>
                          </div>
                          <p>{drawer.type}</p>
                          <div className="bead-pile">
                            {[...Array(12)].map((_, i) => (
                              <div 
                                key={i}
                                className="bead"
                                style={{ 
                                  backgroundColor: drawer.color,
                                  left: `${Math.random() * 60}%`,
                                  top: `${Math.random() * 60}%`,
                                  animationDelay: `${i * 0.1}s`
                                }}
                              ></div>
                            ))}
                          </div>
                          <div className="drawer-links">
                            <button 
                              className="link-btn external-link"
                              onClick={(e) => handleInternalNavigation(drawer.page, e)}
                            >
                              📖 相關頁面
                            </button>
                            <div 
                              className="draggable-bead"
                              draggable
                              onDragStart={(e) => handleDragStart(drawer, e)}
                              onDragEnd={handleDragEnd}
                              style={{ backgroundColor: drawer.color }}
                              title={`拖曳 ${drawer.name}`}
                            >
                              ➕ 拖曳珠子
                            </div>
                            <button 
                              className="extract-bead-btn"
                              onClick={(e) => extractBead(drawer, e)}
                              style={{ 
                                backgroundColor: drawer.color,
                                boxShadow: `0 0 10px ${drawer.shine}`
                              }}
                            >
                              ✨ 取出珠子
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          <div className="cabinet-controls">
            <button 
              className="control-btn"
              onClick={() => setOpenDrawers({})}
            >
              關閉所有抽屜
            </button>
            <button 
              className="control-btn"
              onClick={() => {
                const allDrawers = {};
                [...leftDrawers, ...rightDrawers].forEach(drawer => {
                  allDrawers[drawer.id] = true;
                });
                setOpenDrawers(allDrawers);
              }}
            >
              打開所有抽屜
            </button>
            <button 
              className="control-btn"
              onClick={() => window.location.href = '/tutorial'}
            >
              📚 教學中心
            </button>
            <button 
              className="control-btn"
              onClick={() => window.location.href = '/patterns'}
            >
              📚 圖案庫
            </button>
          </div>
        </div>

        {/* 右側木質串珠盤區域 */}
        <div className="tray-section">
          <WoodenBeadTray />
        </div>
      </div>

      {/* 取出珠子動畫 */}
      {showExtractionAnimation && extractingBead && (
        <div className="extraction-animation">
          <div className="extraction-bead" style={{ backgroundColor: extractingBead.color }}>
            <div className="bead-shine"></div>
            <div className="bead-reflection"></div>
          </div>
          <div className="extraction-sparkles">
            {[...Array(8)].map((_, i) => (
              <div key={i} className="sparkle" style={{ animationDelay: `${i * 0.1}s` }}></div>
            ))}
          </div>
        </div>
      )}

      {/* 取出的珠子顯示 */}
      {extractedBeads.map((bead) => (
        <div
          key={bead.id}
          className="extracted-bead"
          style={{ 
            backgroundColor: bead.color,
            left: `${Math.random() * 80 + 10}%`,
            top: `${Math.random() * 80 + 10}%`
          }}
        >
          <div className="bead-shine"></div>
          <div className="bead-reflection"></div>
        </div>
      ))}

      {/* 浮空展示的珠子 */}
      {Object.entries(floatingBeads).map(([drawerId, isFloating]) => {
        console.log('渲染浮空珠子:', drawerId, isFloating);
        console.log('抽屜ID類型:', typeof drawerId);
        if (!isFloating) return null;
        
        const allDrawers = [...leftDrawers, ...rightDrawers];
        console.log('所有抽屜ID:', allDrawers.map(d => d.id));
        const drawer = allDrawers.find(d => d.id === drawerId);
        console.log('找到抽屜:', drawer);
        if (!drawer) {
          console.log('未找到抽屜，drawerId:', drawerId);
          console.log('可用的抽屜ID:', allDrawers.map(d => d.id));
          return null;
        }
        
        return (
          <FloatingBead 
            key={drawerId}
            drawer={drawer}
            drawerId={drawerId}
            onClose={() => {
              console.log('關閉浮空展示:', drawerId);
              setFloatingBeads(prev => ({ ...prev, [drawerId]: false }));
              // 同時關閉對應的抽屜
              setOpenDrawers(prev => ({ ...prev, [drawerId]: false }));
            }}
            onDragStart={handleDragStart}
            onDragEnd={handleDragEnd}
          />
        );
      })}
    </div>
  );
};

export default BeadCabinet;
